import React from "react";
import { Pagination, PageItem } from "react-bootstrap";
// import "../containers/OfficePage.scss";

function CustomPagination(props) {
 function paginationItems() {
   let items = [];
   for (let i = props.start; i <= props.end; i++) {
     items.push(
       <PageItem
       className="pagination-boxes"
         key={i}
         onClick={() => {
           props.onPageChange(i);
         }}
         active={i === props.currentPage}
       >
         {i}
       </PageItem>
     );
   }
   return items;
 }
 return (
   <Pagination className={"justify-content-center " + props.pagScssClass}>
     {props.currentPage !== 1 ? (
       <Pagination.Prev
       className="pagination-boxes"
         onClick={() => {
           props.onPagination("prev");
         }}
       ></Pagination.Prev>
     ) : null}
     {props.start !== 1 && props.pages > 5 ? (
       <Pagination.Item
       className="pagination-boxes"
         onClick={() => {
           props.onPageChange(1);
         }}
       >
         {1}
       </Pagination.Item>
     ) : null}
     {props.start !== 1 && props.pages > 5 ? (
       <Pagination.Ellipsis
       className="pagination-boxes"
         onClick={() => {
           props.onPagination("prevEllipsis");
         }}
       />
     ) : null}
     {paginationItems()}
     {props.end !== props.pages && props.pages > 5 ? (
       <Pagination.Ellipsis
       className="pagination-boxes"
         onClick={() => {
           props.onPagination("nextEllipsis");
         }}
       />
     ) : null}
     {props.end !== props.pages && props.pages > 5 ? (
       <Pagination.Item
       className="pagination-boxes"
         onClick={() => {
           props.onPageChange(props.pages);
         }}
       >
         {props.pages}
       </Pagination.Item>
     ) : null}
     {props.currentPage !== props.pages ? (
       <Pagination.Next
       className="pagination-boxes"
         onClick={() => {
           props.onPagination("next");
         }}
       ></Pagination.Next>
     ) : null}
   </Pagination>
 );
}

export default CustomPagination;
