/* eslint-disable react/prop-types */
import React from 'react';
import { Route } from 'react-router-dom';

import NavBar from './Navbar/Navbar';
import SideNav from './SideNav/SideNav';
import { getSession, handleUrl } from '../utils/authService';
import { pageWRTAuthRole } from '../utils/common';

const AuthRoute = ({ component: C, props: cProps, ...rest }) => {
  let firstName = '';
  let lastName = '';
  let orgnizationName = '';
  const sessionData = getSession(cProps.sessionCookie);
  const rememberData = getSession(cProps.rememberCookie);
  const { page, pageNotFound } = cProps;

  if (sessionData) {
    firstName = sessionData.first_name;
    lastName = sessionData.last_name;
    orgnizationName = sessionData.organisation;
  } else if (rememberData) {
    firstName = rememberData.first_name;
    lastName = rememberData.last_name;
    orgnizationName = sessionData.organisation;
  }
  const fullName =
    (firstName && firstName.length > 0) || (lastName && lastName.length > 0)
      ? `${firstName} ${lastName}`
      : 'User';

  const authorizedComponent = pageWRTAuthRole(page)?(
    <>
      <NavBar userName={fullName} />
      <SideNav page={page} orgnizationName={orgnizationName}>
        <Route
          {...rest}
          render={props => <C {...props} {...cProps} inProgress />}
        />
      </SideNav>
    </>
  ): (
    <>
      <Route component={pageNotFound} />
    </>
  );

  return <>{handleUrl(authorizedComponent)}</>;
};
export default AuthRoute;
