import React from 'react';
import PropTypes from 'prop-types';
import pageConfig from '../../utils/pageConfig';
import PageLoader from '../../components/PageLoader/PageLoader';
import PageHeader from '../../components/PageHeader/PageHeader';
import Table from '../../components/Table/Table';
import { Pagination } from 'react-bootstrap';
import { getActionsFormate } from '../../utils/common';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

class Assets extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      perPage: 10,
      currentPage: 1,
      prePage: 1,
    }
  }

  componentDidMount() {
    const { getAssetsList, match, page } = this.props;
    const { actions } = pageConfig[page];
    const { params } = match;
    const type = actions && actions.fetchList;
    getAssetsList(type, { ...params });
  }

  render(){
    const { page, inProgress, Data } = this.props;
    const { pageTitle } = pageConfig[page];
    console.log("Checking data:", Data);
    const Theader = ( <tr>
                            <th>Asset</th>
                            <th>Asset Type</th>
                            <th>Submitted Date</th>
                            <th>Due Date</th>
                            <th>Status</th>
                          </tr>
                        )
    const Tbody = Data.length>0 && Data.map(row =>{
      return (<tr>
        <td ></td>
        <td>Document</td>
        <td>20-10-2019</td>
        <td>12-02-2018</td>
        <td>20</td>
      </tr>)
    })
    return (
      <div>
        <PageLoader inProgress={inProgress} />
        <PageHeader
          {...this.props}
          title={pageTitle}
        />
        <Table header={Theader} body={Tbody}/>
        <Pagination className="custom-pagination justify-content-end mt-3">
          <Pagination.First />
          <Pagination.Prev />
          <Pagination.Item>{1}</Pagination.Item>
          <Pagination.Item>{2}</Pagination.Item>
          <Pagination.Ellipsis />
          <Pagination.Item>{20}</Pagination.Item>
          <Pagination.Next />
          <Pagination.Last />
        </Pagination>
      </div>
    );
  }

}

Assets.propTypes = {
  getAssetsList: PropTypes.func.isRequired,
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired,
  inProgress: PropTypes.bool.isRequired
};

const mapStateToProps = state => ({
  Data: state.AssetsReducerStore.data,
  inProgress: state.AssetsReducerStore.inProgress
});

const mapDispatchToProps = dispatch => {
  return {
    getAssetsList: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Assets)
);
