import ACTIONS from '../../apiConfig/actions.constants';
import { COMPONENTS } from '../../utils/constants';

const initialState = {
  [COMPONENTS.USER]: {}
};


const userDetailsReducerStore = (stateParam, { type, payload }) => {
    const state = stateParam || initialState;
    switch (type) {
        case ACTIONS.USERS.GET_USER_INFO:
            return { ...state, data: payload, inProgress: false };
        case ACTIONS.USERS.GET_USER_INFO_SUCCESS:
            return { ...state, [COMPONENTS.USER]: payload, inProgress: false };
        case ACTIONS.USERS.GET_USER_INFO_FAIL:
            return { ...state, data: payload, inProgress: false };
        default:
            return state;
    }   
}

export default userDetailsReducerStore;
