import React from 'react';
import { InputGroup, FormControl, Button } from 'react-bootstrap';
import PropTypes from 'prop-types';
import DisplayErrors from '../DisplayErrors';
import './AddEditAttributesForm.scss'
const AddEditAttributesForm = props => {
  return (
    <div className="card-text text-left add-edit-user">
      <div className="">
          <div className="mb-2">
              Attribute:
            </div>
          <div className="row">

             <InputGroup size="sm"  className="col col-lg-5 mr-6">
                <FormControl
                  type="text"
                  placeholder="Name"
                  id={props.assetTypeFieldId}
                  value={props.assetTypeFieldValue}
                  name={props.assetTypeFieldId}
                  // onChange={props.handleChange}
                  className=" border"
                />
              </InputGroup>
              <InputGroup size="sm"  className="col col-lg-5">
                <FormControl
                  type="text"
                  placeholder="Value"
                  id={props.assetTypeFieldId}
                  value={props.assetTypeFieldValue}
                  name={props.assetTypeFieldId}
                  // onChange={props.handleChange}
                  className="border"
                />
              </InputGroup>
              <Button variant="danger" size="sm" className="rounded-0 custom-add ml-auto mr-5">
            </Button>
          </div>
          <div className="row">
          <button className="text-capitalize border-0 add-attribute p-0 pl-3 ">
            Add New Attribute
            </button>
          </div>

          <Button
            type="submit"
            className="btn onboarding-btn"
            disabled={props.disabled}
          >
            Submit
          </Button>
        {/* </form> */}
      </div>
    </div>
  );
};

AddEditAttributesForm.propTypes = {
  handleSubmit: PropTypes.func.isRequired,
  formErrors: PropTypes.object.isRequired,
  handleChange: PropTypes.func.isRequired,
  disabled: PropTypes.bool.isRequired,
  organizationFieldValue: PropTypes.string.isRequired,
  organizationFieldId: PropTypes.string.isRequired,
  assetTypeFieldId: PropTypes.string.isRequired,
  assetTypeFieldValue: PropTypes.string.isRequired,
  colorFieldId: PropTypes.string.isRequired,
  colorFieldValue: PropTypes.string.isRequired,
  attributeFieldId: PropTypes.string.isRequired,
  attributeFieldValue: PropTypes.array.isRequired,
  daysFieldId: PropTypes.string.isRequired,
  daysFieldValue: PropTypes.number.isRequired
};

AddEditAttributesForm.defaultProps = {};

export default AddEditAttributesForm;
