import React from 'react';
import { InputGroup, FormControl, Button } from 'react-bootstrap';
import PropTypes from 'prop-types';
import DisplayErrors from '../DisplayErrors';
import CustomTreeSelect from '../CustomTreeSelect/CustomTreeSelect';
import { SUBMIT } from '../../utils/constants';
import ButtonLoader from '../ButtonLoader/ButtonLoader';

const AddEditOrgUnitsForm = props => {
  return (
    <div className="card-text text-left">
      <div className="card p-3">
        <form autoComplete="off">
          <DisplayErrors errors={props.formErrors} />
          <div className="row">
            <div className="col col-lg-4 col-12">
              <InputGroup size="sm">
                <FormControl
                  type="text"
                  placeholder="Org Unit Name"
                  id={props.orgUnitFieldId}
                  value={props.orgUnitFieldValue}
                  name={props.orgUnitFieldId}
                  onChange={props.handleChange}
                />
              </InputGroup>
            </div>
          </div>
          <div className="row mb-4">
            <div className="col col-lg-4 col-12 ml-0">
              <CustomTreeSelect
                onChange={props.handleParents}
                page={props.page}
                mode={props.mode}
                defaultValues={props.prevPrentOrgUnit}
              />
            </div>
          </div>
        </form>
      </div>
      <Button
        type="submit"
        className="btn onboarding-btn"
        disabled={props.disabled || props.inProgress}
        id="add-edit-org-unit-btn"
        onClick={props.handleSubmit}
      >
        {props.inProgress && <ButtonLoader />}
        {!props.inProgress && SUBMIT}
      </Button>
    </div>
  );
};

AddEditOrgUnitsForm.propTypes = {
  handleSubmit: PropTypes.func.isRequired,
  formErrors: PropTypes.object.isRequired,
  orgUnitFieldId: PropTypes.string.isRequired,
  orgUnitFieldValue: PropTypes.string.isRequired,
  handleParents: PropTypes.func.isRequired,
  handleChange: PropTypes.func.isRequired,
  disabled: PropTypes.bool.isRequired,
  page: PropTypes.string.isRequired,
  mode: PropTypes.string.isRequired,
  prevPrentOrgUnit: PropTypes.array.isRequired,
  inProgress: PropTypes.bool.isRequired
};

export default AddEditOrgUnitsForm;
