import React from 'react';
import AddEditUserForm from '../../components/AddEditUserForm/AddEditUserForm';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { checkEmail } from '../../utils/formValidations';
import PageHeader from '../../components/PageHeader/PageHeader';
import pageConfig from '../../utils/pageConfig';
import { getActionsFormate } from '../../utils/common';
import { STORE } from '../../utils/constants';

class AddEditUser extends React.Component{

    FIRST_NAME_FIELD_ID = "aeu-fname";
    LAST_NAME_FIELD_ID = "aeu-lname";
    EMAIL_FIELD_ID = "aeu-email";
    DEPARTMENTS_FIELD_ID = "select-departments"
    constructor(props){
        super(props);
        this.state={
            formErrors:{},
            formValidity:false,
            [this.FIRST_NAME_FIELD_ID] : '',
            [this.LAST_NAME_FIELD_ID] : '',
            [this.EMAIL_FIELD_ID] : '',
            [this.DEPARTMENTS_FIELD_ID]:[],
            errorCode:'',
            disabled:true
        }
    }

  componentDidMount(){
      const {getDepartments, page} = this.props;
      const type = pageConfig[page].actions.getDepartments;
      getDepartments(type);
  }

  validateForm = () => {
    const [emailValidity, emailErrors] = checkEmail(
      this.state[this.EMAIL_FIELD_ID],
      'Email'
    );
    const formValidity = emailValidity;
    const formErrors = { ...emailErrors };

    this.setState({ formValidity, formErrors });

    return formValidity;
  };

  enableSubmit = (email, firstName, lastName, departments) => {
    const disabled = !(email.length > 0 && firstName.length > 0 && lastName.length && departments.length);
    this.setState({ disabled });
  };

    handleChange = event => {
        this.setState(
          {
            [event.target.id]: event.target.value,
            formErrors: {}
          },
          () =>
            this.enableSubmit(
              this.state[this.EMAIL_FIELD_ID],
              this.state[this.FIRST_NAME_FIELD_ID],
              this.state[this.LAST_NAME_FIELD_ID],
              this.state[this.DEPARTMENTS_FIELD_ID]
            )
        );
      };

      handleDepartments = event =>{
        let customEvent = {target:{
          id:this.DEPARTMENTS_FIELD_ID,
          value:event
        }}
        this.handleChange(customEvent);
      }
      handleSubmit = event => {
        event.preventDefault();
        // this.props.dispatch({ type: ACTIONS.AUTH.RESET_STATE });
        if (this.validateForm()) {
          const data = {
            email: this.state[this.EMAIL_FIELD_ID],
            first_name: this.state[this.FIRST_NAME_FIELD_ID],
            last_name: this.state[this.LAST_NAME_FIELD_ID]
          };
          this.submit(data);
        }
      };

      submit = data => {
        // this.setState({ errorCode: '' });
        // this.props.dispatch({ type: ACTIONS.AUTH.LOGIN, data });
      };

    getDepartment=()=>{
      const {userDepartments} = this.props;
      const localDepart = this.state[this.DEPARTMENTS_FIELD_ID];
      if(localDepart.length ===0){
        return userDepartments;
      }else if(localDepart.length === userDepartments.length){
        return localDepart;
      }else{
        let customDepart = localDepart;

        for(let udepart of userDepartments){
          let checkExistDepart = false;
          for(let ldepart of localDepart){
            if(ldepart.id === udepart.id){
              checkExistDepart = true;
            }
          }
          if(!checkExistDepart){
            customDepart.push(udepart);
          }
        }
      }

    }

    render(){
        const {page} = this.props;
        const {pageTitle} = pageConfig[page];
        return (
          <div className="add-edit-user">
            <PageHeader title={pageTitle} />
            <AddEditUserForm 
                handleSubmit={this.handleSubmit}
                handleChange={this.handleChange}
                handleDepartments={this.handleDepartments}
                firstNameFieldId={this.FIRST_NAME_FIELD_ID}
                lastNameFieldId={this.LAST_NAME_FIELD_ID}
                emailFieldId={this.EMAIL_FIELD_ID}
                firstNameFieldValue={this.state[this.FIRST_NAME_FIELD_ID]}
                lastNameFieldValue={this.state[this.LAST_NAME_FIELD_ID]}
                emailFieldValue={this.state[this.EMAIL_FIELD_ID]}
                departmentsFieldValue={this.getDepartment()}
                departmentsFieldId={this.DEPARTMENTS_FIELD_ID}
                formErrors={this.formErrors}
                disabled={this.state.disabled}
            />
          </div>  
        );
    }
}

const mapStateToProps =(state)=>({
    userDepartments : state.addEditUserReducerStore[STORE.GET_USER_DEPARTMENTS]
})

const mapDispatchToProps = dispatch => {
  return {
    getDepartments: (type, data) => {
      dispatch(getActionsFormate(type, data));
    }
  };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(AddEditUser));
