import React from 'react';
import { InputGroup, FormControl, Button } from 'react-bootstrap';
import PropTypes from 'prop-types';
import DisplayErrors from '../DisplayErrors';
import 'react-dropdown-tree-select/dist/styles.css'
import './AddEditUserForm.scss';
import CustomSelect from '../CustomSelect/CustomSelect';
import DropdownTreeSelect from 'react-dropdown-tree-select'
import CustomTreeSelect from '../CustomTreeSelect/CustomTreeSelect';


const AddEditUserForm = props => {

  return (
    <div className="card-text text-left">
      <div className="">
        <form onSubmit={props.handleSubmit}>
          <DisplayErrors errors={props.formErrors} />
          <div className="row mb-2">
            <div className="col col-lg-6 col-12">
            <InputGroup size="sm">
              <InputGroup.Prepend>
                <InputGroup.Text className="pl-0"></InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                type="email"
                placeholder="Email"
                id={props.emailFieldId}
                value={props.emailFieldValue}
                name={props.emailFieldId}
                onChange={props.handleChange}

              />
            </InputGroup>
            </div>
            <div className="col col-0 col-lg-6"></div>
          </div>
          <div className="row">
            <div className="col col-lg-6 col-12">
            <InputGroup size="sm">
              <InputGroup.Prepend>
                <InputGroup.Text className="pl-0"></InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                type="text"
                placeholder="First Name"
                id={props.firstNameFieldId}
                value={props.firstNameFieldValue}
                name={props.firstNameFieldId}
                onChange={props.handleChange}
              />
            </InputGroup>
            </div>
            <div className="col col-lg-6 col-12">
            <InputGroup size="sm">
              <InputGroup.Prepend>
                <InputGroup.Text className="pl-0"></InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                type="text"
                placeholder="Last Name"
                id={props.lastNameFieldId}
                value={props.lastNameFieldValue}
                name={props.lastNameFieldId}
                onChange={props.handleChange}
              />
            </InputGroup>
            </div>
          </div>
          <div className="row mb-4">
            <div className="col col-lg-6 col-12 ml-2">
              <CustomTreeSelect />
            </div>
            <div className="col col-lg-6"></div>
          </div>
          <div className="mb-2 col col-6 ">
          <CustomSelect
            onChange={props.handleDepartments}
            departmentsFieldValue={props.departmentsFieldValue}
            departmentsFieldId={props.departmentsFieldId}
          />
          </div>
          <Button
            type="submit"
            className="btn onboarding-btn"
            disabled={props.disabled}
          >
            Submit
          </Button>
        </form>
      </div>
    </div>
  );
};

AddEditUserForm.propTypes = {
  handleSubmit: PropTypes.func.isRequired,
  formErrors: PropTypes.object.isRequired,
  emailFieldId: PropTypes.string.isRequired,
  emailFieldValue: PropTypes.string.isRequired,
  handleChange: PropTypes.func.isRequired,
  firstNameFieldId: PropTypes.string.isRequired,
  firstNameFieldValue: PropTypes.string.isRequired,
  lastNameFieldId: PropTypes.string.isRequired,
  lastNameFieldValue: PropTypes.string.isRequired,
  handleDepartments: PropTypes.func.isRequired,
  departmentsFieldValue: PropTypes.string.isRequired,
  departmentsFieldId: PropTypes.string.isRequired,
  disabled: PropTypes.bool.isRequired
};

AddEditUserForm.defaultProps = {};

export default AddEditUserForm;
