import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import pageConfig from '../../utils/pageConfig';
import PageHeader from '../../components/PageHeader/PageHeader';
import { getActionsFormate } from '../../utils/common';
import DataCards from '../../components/DataCards/DataCards';
import { DATA_NOT_FOUND } from '../../utils/constants';
import PageLoader from '../../components/PageLoader/PageLoader';
import { getBodyComponenet } from '../../utils/utils';
import { checkUserValidity } from '../../utils/authService';

class ListPage extends React.Component {
  componentDidMount() {
    const { makeCall, page } = this.props;
    const types = pageConfig[page].actions.fetchList;
    let data = {};
    data = { department: this.props.match.params.orgUnitId, page };
    makeCall(types, data);
  }

  componentDidUpdate(prevProps) {
    if (prevProps.match.url !== this.props.match.url) {
      const { makeCall, page } = this.props;
      const types = pageConfig[page].actions.fetchList;
      let data = {};
      data = { department: this.props.match.params.orgUnitId, page };
      makeCall(types, data);
    }
  }

  render() {
    const { page, dataCard, inProgress } = this.props;
    const { headerActions, bodyHandler, redirect } = pageConfig[page];
    const { pageTitle } = pageConfig[page];
    const isDataFound =
      dataCard && dataCard instanceof Array && dataCard.length > 0;
    const dataCardComponent = (
      <DataCards {...this.props} data={dataCard} actions={bodyHandler} />
    );
    const isAssetAdmin =
    checkUserValidity(false, ROLES.ASSET_MANAGER) &&
    !checkUserValidity(false, ROLES.SUPER_ADMIN) &&
    !checkUserValidity(false, ROLES.ORG_ADMIN);
    return (
      <div className="list-page" id="list-of-data">
        <PageLoader inProgress={inProgress} />
        <PageHeader
          {...this.props}
          title={pageTitle}
          actions={isAssetAdmin?[]:headerActions}
          redirect={redirect}
        />
        {getBodyComponenet(
          dataCardComponent,
          isDataFound,
          DATA_NOT_FOUND[page]
        )}
      </div>
    );
  }
}

ListPage.propTypes = {
  page: PropTypes.string,
  dataCard: PropTypes.oneOfType([PropTypes.array, PropTypes.object]),
  makeCall: PropTypes.func,
  match: PropTypes.any,
  // eslint-disable-next-line react/require-default-props
  title: PropTypes.string,
  inProgress: PropTypes.bool
};

ListPage.defaultProps = {};

const mapStateToProps = (state, props) => {
  const { page } = props;
  const respectedStore = pageConfig[page].store.storedList;
  const formateFunction = pageConfig[page].formateFunc;

  return {
    dataCard: formateFunction(state.listReducerStore[respectedStore]),
    title: state.listReducerStore.title,
    inProgress: state.listReducerStore.inProgress
  };
};

const mapDispatchToProps = dispatch => {
  return {
    makeCall: (type, data) => {
      dispatch(getActionsFormate(type, data));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(ListPage)
);
