import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import pageConfig from '../../utils/pageConfig';
import PageHeader from '../../components/PageHeader/PageHeader';
import { getActionsFormate } from '../../utils/common';
import DataCards from '../../components/DataCards/DataCards';
import { PAGE } from '../../utils/constants';
import LoaderComponent from '../../components/Loader/LoaderComponent';
import PageLoader from '../../components/PageLoader/PageLoader';

class ListPage extends React.Component {
  componentDidMount() {
    const { makeCall, page } = this.props;
    const types = pageConfig[page].actions.fetchList;
    let data = {};
    data = { department: this.props.match.params.orgUnitId };
    makeCall(types, data);
  }

  componentDidUpdate(prevProps) {
    if (prevProps.match.url !== this.props.match.url) {
      const { makeCall, page } = this.props;
      const types = pageConfig[page].actions.fetchList;
      let data = {};
      data = { department: this.props.match.params.orgUnitId };
      makeCall(types, data);
    }
  }

  render() {
    const { page, dataCard, title, inProgress } = this.props;
    const { headerActions, bodyHandler, redirect } = pageConfig[page];

    const pageTitle =
      page === PAGE.ORG_UNITS_DETAILS
        ? title || pageConfig[page].pageTitle
        : pageConfig[page].pageTitle;
    return (
      <div className="list-page">
        <PageLoader inProgress={inProgress} />
        <PageHeader
          {...this.props}
          title={pageTitle}
          actions={headerActions}
          redirect={redirect}
        />
        <DataCards {...this.props} data={dataCard} actions={bodyHandler} />
      </div>
    );
  }
}

ListPage.propTypes = {
  page: PropTypes.string.isRequired,
  dataCard: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired,
  makeCall: PropTypes.func.isRequired,
  match: PropTypes.any.isRequired,
  // eslint-disable-next-line react/require-default-props
  title: PropTypes.string,
  inProgress: PropTypes.bool.isRequired
};

ListPage.defaultProps = {};

const mapStateToProps = (state, props) => {
  const { page } = props;
  const respectedStore = pageConfig[page].store.storedList;
  const formateFunction = pageConfig[page].formateFunc;

  return {
    dataCard: formateFunction(state.listReducerStore[respectedStore]),
    title: state.listReducerStore.title,
    inProgress: state.listReducerStore.inProgress
  };
};

const mapDispatchToProps = dispatch => {
  return {
    makeCall: (type, data) => {
      dispatch(getActionsFormate(type, data));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(ListPage)
);
