import React from 'react';
import {
  InputGroup,
  FormControl,
  Button,
  FormGroup,
  FormLabel
} from 'react-bootstrap';
import PropTypes from 'prop-types';
import DisplayErrors from '../DisplayErrors';
import { SUBMIT } from '../../utils/constants';
import ButtonLoader from '../ButtonLoader/ButtonLoader';
import { isValidData } from '../../utils/common';

const GenerateQrCodesForm = props => {
  const { assetTypesList } = props;
  const assetTypes =
    assetTypesList &&
    assetTypesList.length > 0 &&
    assetTypesList.map(each => {
      if (isValidData(each)) {
        const { id, name } = each;
        return (
          <option value={id} key={id}>
            {name}
          </option>
        );
      }
      return null;
    });
  return (
    <div className="card-text text-left">
      <div className="card p-3">
        <form autoComplete="off">
          <DisplayErrors errors={props.formErrors} />
          <div className="row">
            <div className="col col-lg-4 col-12">
              <FormGroup controlId="formGridState">
                <FormLabel> Asset Type </FormLabel>
                <FormControl
                  as="select"
                  onChange={props.handleChange}
                  defaultValue="Select a Value"
                  id={props.assetTypeId}
                  name={props.assetTypeId}
                >
                  <option value="Select a Value" disabled>
                    Choose Asset Type
                  </option>
                  {assetTypes}
                </FormControl>
              </FormGroup>
            </div>
          </div>
          <div className="row mb-4">
            <div className="col col-lg-4 col-12 ml-0">
              <FormLabel> Count </FormLabel>

              <InputGroup size="sm">
                <FormControl
                  type="number"
                  id={props.qrCodesCountId}
                  value={props.qrCodesCountValue}
                  name={props.qrCodesCountId}
                  onChange={props.handleChange}
                />
              </InputGroup>
            </div>
          </div>
        </form>
      </div>
      <Button
        type="submit"
        className="btn onboarding-btn"
        disabled={props.disabled || props.inProgress}
        id="add-edit-org-unit-btn"
        onClick={props.handleSubmit}
      >
        {props.inProgress && <ButtonLoader />}
        {!props.inProgress && SUBMIT}
      </Button>
    </div>
  );
};

GenerateQrCodesForm.propTypes = {
  handleSubmit: PropTypes.func.isRequired,
  formErrors: PropTypes.object,
  disabled: PropTypes.bool.isRequired,
  inProgress: PropTypes.bool.isRequired,
  assetTypeId: PropTypes.string.isRequired,
  qrCodesCountId: PropTypes.string.isRequired,
  qrCodesCountValue: PropTypes.string.isRequired,
  handleChange: PropTypes.func.isRequired,
  assetTypesList: PropTypes.array.isRequired
};

GenerateQrCodesForm.defaultProps = {
 formErrors: {}
};
export default GenerateQrCodesForm;
