import React from 'react';
import PropTypes from 'prop-types';
import './UserInfo.scss';
import { getOrgUnitUrl } from '../../utils/utils';

const UserInfo = props => {
  const { userData } = props;
  const email = userData && userData.email ? userData.email : '';
  const departments =
    userData &&
    userData.department &&
    userData.department.map(depart => {
      const Id = depart.id;
      return (
        <div className="user-departments-values mb-2">
          {'-'}
          {depart.name && <a href={getOrgUnitUrl(Id, props)}>{depart.name}</a>}

        </div>
      );
    });
  return (
    <div className="user-info">
      <div className="row">
        <div className="user-email-header mb-3 col col-2">Email:</div>
        <div className="user-email-value mb-3 col col-10">{email}</div>
      </div>

      <div className="row">
        <div className="user-departmens-header mb-3 col col-2">
          {' '}
          Departments:
        </div>
        <div className="col col-10"> {departments} </div>
      </div>
    </div>
  );
};

UserInfo.propTypes = {
  userData: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired
};

export default UserInfo;
