// name: "Nowshad"
function getRecursiveData(data){
  for(let dData of data){
    dData.label = dData.name;
    dData.orgUnitId = dData.id;
    delete dData.name;
    delete dData.id;
    dData.children = dData.child;
    if( dData.child &&  dData.child.length > 0){
      delete dData.child;
      getRecursiveData(dData.children);
    }else{
      delete dData.child;
    }
 }

  return data;
}

const getFomatedOrgUnitData = (OrgUnitData) =>{
  let formatedData = [];
  if(OrgUnitData && OrgUnitData.length>0){
    formatedData = getRecursiveData(OrgUnitData);
  }
  console.log("formated", formatedData);
  return formatedData;
}

export {
  getUserCard,
  showUserDetails,
  getOrgUnits,
  getOrgUnitsNext,
  redirect,
  getFormatOfdData,
  redirectToDepartments,
  getAssetAttributes,
  getAssetTypes,
  getBreadcrumbs,
  getOrgUnitUrl,
  redirectAssetTypeDetail,
  redirectAssetAttributeDetail,
  getFomatedOrgUnitData
};
