import React from "react";
import { Pagination, PageItem } from "react-bootstrap";
// import "../containers/OfficePage.scss";

function CustomPagination(props) {
  const { start, end, currentPage, totalPages, onPagination} = 
 function paginationItems() {
   let items = [];
   for (let i = start; i <= end; i++) {
     items.push(
       <PageItem
       className="pagination-boxes"
         key={i}
         onClick={() => {
           onPageChange(i);
         }}
         active={i === currentPage}
       >
         {i}
       </PageItem>
     );
   }
   return items;
 }
 return (
   <Pagination className={"justify-content-center mt-3"}>
     {currentPage !== 1 ? (
       <Pagination.Prev
       className="pagination-boxes"
         onClick={() => {
           onPagination("prev");
         }}
       ></Pagination.Prev>
     ) : null}
     {start !== 1 && totalPages > 5 ? (
       <Pagination.Item
       className="pagination-boxes"
         onClick={() => {
           onPageChange(1);
         }}
       >
         {1}
       </Pagination.Item>
     ) : null}
     {start !== 1 && totalPages > 5 ? (
       <Pagination.Ellipsis
       className="pagination-boxes"
         onClick={() => {
           onPagination("prevEllipsis");
         }}
       />
     ) : null}
     {paginationItems()}
     {end !== totalPages && totalPages > 5 ? (
       <Pagination.Ellipsis
       className="pagination-boxes"
         onClick={() => {
           onPagination("nextEllipsis");
         }}
       />
     ) : null}
     {end !== totalPages && totalPages > 5 ? (
       <Pagination.Item
       className="pagination-boxes"
         onClick={() => {
           console.log("clicked");

           onPageChange(totalPages);
         }}
       >
         {totalPages}
       </Pagination.Item>
     ) : null}
     {currentPage !== totalPages ? (
       <Pagination.Next
       className="pagination-boxes"
         onClick={() => {
           onPagination("next");
         }}
       ></Pagination.Next>
     ) : null}
   </Pagination>
 );
}

export default CustomPagination;
