import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import pageConfig from '../../utils/pageConfig';
import PageHeader from '../../components/PageHeader/PageHeader';
import { getActionsFormate } from '../../utils/common';
import DataCards from '../../components/DataCards/DataCards';

class ListPage extends React.Component {
  componentDidMount() {
    const { makeCall, actionType, page } = this.props;
    if (actionType) {
      const { type } = pageConfig[page][actionType];
      makeCall(type);
    }
  }

  render() {
    const { actionType, page } = this.props;
    const { pageTitle, actions } = pageConfig[page];
    const { handler } = actionType ? pageConfig[page][actionType] : false;
    const { data } = this.props.listData;
    return (
      <div className="list-page">
        <PageHeader title={pageTitle} actions={actions} />
        <DataCards cardData={{ data, handler }} />
        <div>Hello world</div>
      </div>
    );
  }
}

ListPage.propTypes = {
  page: PropTypes.string.isRequired,
  actionType: PropTypes.string.isRequired,
  makeCall: PropTypes.func.isRequired,
  listData: PropTypes.object.isRequired
};

ListPage.defaultProps = {};

const mapStateToProps = state => ({
  listData: state.listReducerStore
});
const mapDispatchToProps = dispatch => {
  return {
    makeCall: (type, data) => {
      dispatch(getActionsFormate(type, data));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(ListPage)
);
