import React from 'react';
import PropTypes from 'prop-types';
import './UserInfo.scss';
import DataCards from '../DataCards/DataCards';

const UserInfo = props => {
  const { userData, bodyHandler } = props;
  console.log("user data:", userData);

  const email = userData && userData.email ? userData.email : '';
  const departments = userData && userData.department && userData.department.map(depart=>{
    const Id = depart.id; 
    const title = depart.name ? depart.name:"Department";
    const body = (<div className="card-data-body user-email">Body</div>);
    return {Id, title, body};
  })
  return (
    <div className="user-info">
      <div className="row">
        <div className="user-email mb-3">Email:</div>
        <div className="user-email mb-3">{email}</div>
      </div>
      <div className="user-email mb-3"> Departments:</div>
      <DataCards {...props} data={departments} actions={bodyHandler} />
    </div>
  );
};

UserInfo.propTypes = {
  userData: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired
};

export default UserInfo;
