import { include } from 'named-urls';
import {
  faObjectGroup,
  faBuilding,
  faObjectUngroup
} from '@fortawesome/free-regular-svg-icons';
import {
  faUsers,
  faShapes,
  faChartLine
} from '@fortawesome/free-solid-svg-icons';
import { ROLES, PAGE } from './utils/constants';

const userSessionCookie = 'upayogakarta';
const rememberUserCookie = 'yaadrakhna';
const assets = include('/assets/', {
  LIST: '',
  ADD: 'add/',
  DETAIL: include(':assetId/', {
    SHOW: '',
    MODIFY: 'modify/'
  })
});

const FOOTPRINTS_ROUTES = {
  LOGIN: '/login/',
  ANALYTICS: '/analytics/',
  DASHBOARD: '/dashboard/',

  ORG_UNITS: include('/org-units/', {
    LIST: '',
    ADD: 'add/',
    DETAIL: include(':orgUnitId/', {
      SHOW: '',
      MODIFY: 'modify/',
      ASSET: include('assets/', {
        LIST: '',
        ADD: 'add/',
        DETAIL: include(':assetId/', {
          SHOW: '',
          MODIFY: 'modify/'
        })
      }),
      ANALYTICS: 'analytics/'
    })
  }),

  USERS: include('/users/', {
    LIST: '',
    ADD: 'add/',
    DETAIL: include(':userId/', {
      SHOW: '',
      MODIFY: 'modify/'
    })
  }),

  ASSET_TYPES: include('/asset-types/', {
    LIST: '',
    ADD: 'add/',
    DETAIL: include(':assetTypeId/', {
      SHOW: '',
      MODIFY: 'modify/'
    })
  }),

  ASSET_ATTRIBUTES: include('/asset-attrs/', {
    LIST: '',
    ADD: 'add/',
    DETAIL: include(':assetAttrId/', {
      SHOW: '',
      MODIFY: 'modify/'
    })
  }),

  ASSET: include('/assets/', {
    LIST: '',
    ADD: 'add/',
    DETAIL: include(':assetId/', {
      SHOW: '',
      MODIFY: 'modify/'
    })
  }),

  QR_CODES: include('/qr-codes/', {
    LIST: '',
    ADD: 'add/'
  }),
  FORGOT_PASSWORD: '/forgot-password',
  PASSWORD_RESET: '/password-reset/:token'
};

const SIDENAV_ITEMS = [
  {
    position: 1,
    displayName: 'Analytics',
    route: FOOTPRINTS_ROUTES.ANALYTICS,
    icon: faChartLine,
    id: 'analytics',
    page: PAGE.ANALYTICS
  },
  {
    position: 2,
    displayName: 'Org Units',
    route: FOOTPRINTS_ROUTES.ORG_UNITS.LIST,
    icon: faBuilding,
    id: 'org-untis',
    page: PAGE.ORG_UNITS
  },
  {
    position: 3,
    displayName: 'Users',
    route: FOOTPRINTS_ROUTES.USERS.LIST,
    icon: faUsers,
    id: 'users',
    page: PAGE.USERS
  },
  {
    position: 5,
    displayName: 'Asset Attributes',
    route: FOOTPRINTS_ROUTES.ASSET_ATTRIBUTES.LIST,
    icon: faShapes,
    id: 'asset-attributes',
    page: PAGE.ASSET_ATTRIBUTES
  },
  {
    position: 4,
    displayName: 'Asset Types',
    route: FOOTPRINTS_ROUTES.ASSET_TYPES.LIST,
    icon: faObjectGroup,
    id: 'asset-types',
    page: PAGE.ASSET_TYPES
  },
  {
    position: 5,
    displayName: 'Assets',
    route: FOOTPRINTS_ROUTES.ASSET.LIST,
    icon: faObjectUngroup,
    id: 'assets',
    page: PAGE.ASSET
  },
  {
    position: 6,
    displayName: 'Generate Qr Codes',
    route: FOOTPRINTS_ROUTES.QR_CODES.ADD,
    icon: faObjectUngroup,
    id: 'qr-codes',
    page: PAGE.QR_CODES
  }
];

const ASSET_MNAGER_URLS = [
  FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.SHOW,
  FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.ASSET.LIST,
  FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.ASSET.DETAIL.SHOW,
  FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.ANALYTICS
];

const PAGE_ACCESS = {
  [ROLES.SUPER_ADMIN]: [Object.values(PAGE)],
  [ROLES.ORG_ADMIN]: [Object.values(PAGE)],
  [ROLES.ORG_UNIT_ADMIN]: [Object.values(PAGE)],
  [ROLES.ASSET_MANAGER]: [
    [
      PAGE.ANALYTICS,
      PAGE.ORG_UNITS,
      PAGE.ORG_UNITS_DETAILS,
      PAGE.ADD_ORG_UNIT,
      PAGE.UPDATE_ORG_UNIT,
      PAGE.ASSET,
      PAGE.ASSET_DETAILS,
      PAGE.ADD_QR_CODES
    ]
  ],
  [ROLES.EMPLOYEE]: [Object.values(PAGE)]
};

export {
  userSessionCookie,
  rememberUserCookie,
  FOOTPRINTS_ROUTES,
  SIDENAV_ITEMS,
  PAGE_ACCESS,
  ASSET_MNAGER_URLS
};
