import React from 'react';
import { InputGroup, FormControl, Button } from 'react-bootstrap';
import PropTypes from 'prop-types';
import DisplayErrors from '../DisplayErrors';
import CustomSelect from '../CustomSelect/CustomSelect';


const AddEditOrgUnitsForm = props => {
  return (
    <div className="card-text text-left">
      <div className="">
        <form onSubmit={props.handleSubmit}>
          <DisplayErrors errors={props.formErrors} />
          <div className="row">
            <div className="col col-lg-4 col-12">
              <InputGroup size="sm">
                <FormControl
                  type="text"
                  placeholder="Org Unit Name"
                  id={props.orgUnitFieldId}
                  value={props.orgUnitFieldValue}
                  name={props.orgUnitFieldId}
                  onChange={props.handleChange}
                />
              </InputGroup>
            </div>
          </div>
          <CustomSelect
            onChange={props.handleParents}
            fieldValues={props.parentFieldValues}
            fieldId={props.parentFieldIds}
          />
          <Button
            type="submit"
            className="btn onboarding-btn"
            disabled={props.disabled}
          >
            Submit
          </Button>
        </form>
      </div>
    </div>
  );
};

AddEditOrgUnitsForm.propTypes = {
  handleSubmit: PropTypes.func.isRequired,
  formErrors: PropTypes.object.isRequired,
  orgUnitFieldId: PropTypes.string.isRequired,
  orgUnitFieldValue: PropTypes.string.isRequired,
  handleParents: PropTypes.func.isRequired,
  parentFieldValues: PropTypes.array.isRequired,
  parentFieldIds: PropTypes.string.isRequired,
  handleChange: PropTypes.func.isRequired,
  disabled: PropTypes.bool.isRequired
};


export default AddEditOrgUnitsForm;
