import { reverse } from 'named-urls';
import { PAGE, STORE } from './constants';
import ACTIONS from '../apiConfig/actions.constants';
import { FOOTPRINTS_ROUTES } from '../config';
import UserInfo from '../components/UserInfo/UserInfo';
import {
  showUserDetails,
  getOrgUnitsNext,
  getAssetAttributes,
  getAssetTypes,
  redirect,
  getOrgUnits,
  getUserCard
} from './utils';

const ADD = 'add';
const EDIT = 'Edit';
const USERS_PAGE_TITLE = 'Users';
const ORG_UNITS_PAGE_TITLE = 'Org Units';
const ORG_UNITS_DETAILS_PAGE_TITLE = 'Org Units Details';
const ASSET_TYPE_PAGE_TITLE = 'Asset Types';
const ASSET_ATTRIBUTES_PAGE_TITLE = 'Asset Attributes';
const USER_DETAILS = 'Details';
const ADD_USER = 'Add User';
const UPDATE_USER = 'Update User';
const ADD_ORG_UNIT = 'Add Org Units';
const assetTypeAdd = () => 'hellowrold';
const orgUnitAdd = () => 'org unit add';
const assetAttrAdd = () => 'Asset attr add';

export default {
  [PAGE.ASSET_TYPES]: {
    pageTitle: ASSET_TYPE_PAGE_TITLE,
    headerActions: [
      {
        text: ADD,
        handler: assetTypeAdd
      }
    ],
    actions: {
      fetchList: ACTIONS.ASSET_TYPES.GET_ASSET_TYPES
    },
    store: {
      storedList: STORE.ASSET_TYPES
    },
    bodyHandler: {
      listHandler: getAssetTypes
    },
    formateFunc: getOrgUnits
  },

  [PAGE.USERS]: {
    pageTitle: USERS_PAGE_TITLE,
    headerActions: [
      {
        text: ADD,
        handler: redirect
      }
    ],
    actions: {
      fetchList: ACTIONS.USERS.GET_USER_LIST
    },
    store: {
      storedList: STORE.USER_LIST
    },
    bodyHandler: {
      listHandler: showUserDetails
    },
    redirect: {
      [ADD]: reverse(FOOTPRINTS_ROUTES.USERS.ADD)
    },
    formateFunc: getUserCard
  },

  [PAGE.ORG_UNITS]: {
    pageTitle: ORG_UNITS_PAGE_TITLE,
    headerActions: [
      {
        text: ADD,
        handler: orgUnitAdd
      }
    ],
    actions: {
      fetchList: ACTIONS.ORG_UNITS.GET_ORG_UNITS_LIST
    },
    store: {
      storedList: STORE.ORG_UNITS_LIST
    },
    bodyHandler: {
      listHandler: getOrgUnitsNext
    },
    formateFunc: getOrgUnits
  },

  [PAGE.ORG_UNITS_DETAILS]: {
    pageTitle: ORG_UNITS_DETAILS_PAGE_TITLE,
    headerActions: [
      {
        text: ADD,
        handler: orgUnitAdd
      }
    ],
    actions: {
      fetchList: ACTIONS.ORG_UNITS.GET_ORG_UNITS_DETAILS
    },
    store: {
      storedList: STORE.ORG_UNITS_LIST
    },
    bodyHandler: {
      listHandler: getOrgUnitsNext
    },
    formateFunc: getOrgUnits
  },

  [PAGE.ASSET_ATTRIBUTES]: {
    pageTitle: ASSET_ATTRIBUTES_PAGE_TITLE,
    headerActions: [
      {
        text: ADD,
        handler: assetAttrAdd
      }
    ],
    actions: {
      fetchList: ACTIONS.ASSET_ATTRIBUTES.GET_ASSET_ATTRIBUTES
    },
    store: {
      storedList: STORE.ASSET_ATTRIBUTES
    },
    bodyHandler: {
      listHandler: getAssetAttributes
    },
    formateFunc: getOrgUnits
  },

  [PAGE.USER_DETAILS]: {
    pageTitle: USER_DETAILS,
    headerActions: [
      {
        text: EDIT,
        handler: redirect
      }
    ],
    redirect: {
      [ADD]: reverse(FOOTPRINTS_ROUTES.USERS.DETAIL.SHOW)
    },
    component: UserInfo,
    actions: {
      fetchDetails: ACTIONS.USERS.GET_USER_INFO
    }
  },

  [PAGE.ADD_USER]: {
    pageTitle: ADD_USER,
    headerActions: [],
    actions: {
      postData: ACTIONS.USERS.ADD_USER,
      getDepartments: ACTIONS.USERS.GET_USER_DEPARTMENTS,
      fetchUser: ''
    },
    store: {}
  },

  [PAGE.UPDATE_USER]: {
    pageTitle: UPDATE_USER,
    headerActions: [],
    actions: {
      postData: ACTIONS.USERS.UPDATE_USER,
      getDepartments: ACTIONS.USERS.GET_USER_DEPARTMENTS,
      fetchUser: ACTIONS.USERS.GET_USER_INFO
    }
  },

  [PAGE.ADD_ORG_UNIT]: {
    pageTitle: ADD_ORG_UNIT,
    headerActions:[],
    actions: {
      postData: ACTIONS.ORG_UNITS.ADD_ORG_UNIT,
      getParentOrgUnits: ACTIONS.USERS.GET_USER_DEPARTMENTS
    }
  }


};
