import { call, put } from 'redux-saga/effects';
import ACTIONS from '../../apiConfig/actions.constants';
import { FOOTPRINTS_API } from '../../apiConfig/api.config';

function* generateQrCodes(action) {
  let response;
  try {
    response = yield call(
      window.axios.postData,
      FOOTPRINTS_API.GENERATE_QR_CODES,
      action.data
    );

    if (response.status === 201 || response.status === 200) {
      let isStatusPending = true;
      while(isStatusPending){
       let pdfResponce = yield call(
          window.axios.getData,
          `${FOOTPRINTS_API.CHECK_PDF_STATUS}${response.action.pdf_id}`,
        );
        if(pdfResponce.status === 201 || pdfResponce.status === 200){
          if(pdfResponce.data.status === "Completed"){
            isStatusPending = false;
          }
        }else{
          throw Error;
        }
      }
      yield put({
        type: ACTIONS.QR_CODES.ADD_QR_CODES_SUCCESS,
        payload: response.data
      });
      yield put({
        type: ACTIONS.ALERT_MESSAGE.ADD_SUCCESS
      });
    } else {
      yield put({
        type: ACTIONS.QR_CODES.ADD_QR_CODES_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.QR_CODES.ADD_QR_CODES_FAIL, payload: e });
  }
}

const dummy = {};

export { generateQrCodes, dummy };
