import React from 'react';
import { InputGroup, FormControl, Button } from 'react-bootstrap';
import PropTypes from 'prop-types';
import DisplayErrors from '../DisplayErrors';
import 'react-dropdown-tree-select/dist/styles.css';
import './AddEditUserForm.scss';
import CustomSelect from '../CustomSelect/CustomSelect';
import CustomTreeSelect from '../CustomTreeSelect/CustomTreeSelect';
import { PAGE, SUBMIT } from '../../utils/constants';
import ButtonLoader from '../ButtonLoader/ButtonLoader';

const AddEditUserForm = props => {
  return (
    <div className="card-text text-left add-edit-user border p-3 border-2">
      <div className="">
        <form onSubmit={props.handleSubmit}>
          <DisplayErrors errors={props.formErrors} />
          <div className="row mb-2 ">
            <div className="col col-lg-4 col-12">
              <InputGroup size="sm">
                <FormControl
                  type="email"
                  placeholder="Email"
                  id={props.emailFieldId}
                  value={props.emailFieldValue}
                  name={props.emailFieldId}
                  onChange={props.handleChange}
                  readOnly={props.page === PAGE.UPDATE_USER}
                />
              </InputGroup>
            </div>
            <div className="col col-0 col-lg-4" />
          </div>
          <div className="row">
            <div className="col col-lg-4 col-12">
              <InputGroup size="sm">
                <FormControl
                  type="text"
                  placeholder="First Name"
                  id={props.firstNameFieldId}
                  value={props.firstNameFieldValue}
                  name={props.firstNameFieldId}
                  onChange={props.handleChange}
                />
              </InputGroup>
            </div>
            <div className="col col-lg-4 col-12">
              <InputGroup size="sm">
                <FormControl
                  type="text"
                  placeholder="Last Name"
                  id={props.lastNameFieldId}
                  value={props.lastNameFieldValue}
                  name={props.lastNameFieldId}
                  onChange={props.handleChange}
                />
              </InputGroup>
            </div>
          </div>
          <div className="row mb-4">
            <div className="col col-lg-4 col-12 ml-0">
              <CustomTreeSelect
                onChange={props.handleOrgUnit}
                page={props.page}
                mode={props.mode}
                defaultValues={props.selectedOrgUnits}
              />
            </div>
          </div>
          <CustomSelect
            onChange={props.handleChangeRoles}
            fieldValues={props.userRoleValues}
            fieldId={props.userRolesId}
            placeholder="Roles"
          />
          <Button
            type="submit"
            className="btn onboarding-btn"
            disabled={props.disabled || props.inProgress}
            id="add-edit-submit-btn"
          >
            {props.inProgress && <ButtonLoader />}
            {!props.inProgress && SUBMIT}
          </Button>
        </form>
      </div>
    </div>
  );
};

AddEditUserForm.propTypes = {
  handleSubmit: PropTypes.func.isRequired,
  formErrors: PropTypes.object.isRequired,
  emailFieldId: PropTypes.string.isRequired,
  emailFieldValue: PropTypes.string.isRequired,
  handleChange: PropTypes.func.isRequired,
  firstNameFieldId: PropTypes.string.isRequired,
  firstNameFieldValue: PropTypes.string.isRequired,
  lastNameFieldId: PropTypes.string.isRequired,
  lastNameFieldValue: PropTypes.string.isRequired,
  handleOrgUnit: PropTypes.func.isRequired,
  disabled: PropTypes.bool.isRequired,
  handleChangeRoles: PropTypes.func.isRequired,
  userRoleValues: PropTypes.array.isRequired,
  userRolesId: PropTypes.string.isRequired,
  mode: PropTypes.string.isRequired,
  selectedOrgUnits: PropTypes.array.isRequired,
  page: PropTypes.string.isRequired,
  inProgress: PropTypes.bool.isRequired
};

AddEditUserForm.defaultProps = {};

export default AddEditUserForm;
