import React from 'react';
import PropTypes from 'prop-types';
import './AssetDetails.scss';
import { getFormatedDate } from '../../utils/common';

const AssetDetails = props => {
  const { Data } = props;
  const {
    asset_type: AssetType,
    attributes: Attributes,
    due_date: DueDate,
    state: Status,
    submitted_date: SubmittedDate
  } = Data;
  let customKey = 0;
  const attributeElements =
    Attributes &&
    Attributes.map(each => {
      const { attribute: Attribute, attribute_value: AttributeValue } = each;
      customKey += 1;
      return (
        <div className="asset-details-body row" key={customKey}>
          <span className="asset-details-key col">{Attribute.name}</span>
          <span className="asset-details-value col">{AttributeValue.name}</span>
        </div>
      );
    });
  return (
    <div className="asset-details-page">
      <div className="row">
        <div className="col col-12 col-lg-5 col-md-5 ml-2 p-2 details-title">
          {' '}
          Details{' '}
        </div>
      </div>
      <div className="row text-capitalize">
        <div className="col col-12 col-lg-7 col-md-5 mb-4 ml-2 p-2 asset-detials">
          <div className="asset-details-body row">
            <span className="asset-details-key col">Asset Type</span>
            <span className="asset-details-value col">{AssetType}</span>
          </div>
          <div className="asset-details-body row">
            <span className="asset-details-key col">Submitted Date:</span>
            <span className="asset-details-value col">{getFormatedDate(SubmittedDate)}</span>
          </div>
          <div className="asset-details-body row">
            <span className="asset-details-key col">Due Date</span>
            <span className="asset-details-value col">{getFormatedDate(DueDate)}</span>
          </div>
          <div className="asset-details-body row">
            <span className="asset-details-key col">Status</span>
            <span className="asset-details-value col">{Status}</span>
          </div>

          <div id="asset-attributs-title" className="mb-3 mt-3">
            <span>Attributes</span>
          </div>
          {attributeElements}
        </div>
        <div className="col col-11 col-md-5 col-lg-4 mr-md-5 ml-3 ml-md-auto p-0 asset-history-detials sticky-top">
          <div className="history-title sticky-top">History</div>
          <div className="events w-100">
            <div className="history-item">
              <span className="date-time">10:03</span>
              <span className="item-data">
                <strong>Bat &amp; Ball</strong> On time
              </span>
            </div>
            <div className="history-item">
              <span className="date-time">10:03</span>
              <span className="item-data">
                <strong>Bat &amp; Ball</strong> On time and other text that may
                span over 2 lines
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

AssetDetails.propTypes = {
  Data: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired
};

export default AssetDetails;
