import React from 'react';
import { InputGroup, FormControl, Button, Form } from 'react-bootstrap';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import DisplayErrors from '../DisplayErrors';
import './AddEditAssetTypesForm.scss';
import { SUBMIT } from '../../utils/constants';
import CustomButton from '../CustomButton/CustomButton';

const AddEditAssetTypesForm = props => {
  return (
    <div>
      <div className="card border">
        <div className="row card-body card-text text-left">
          <div className=" col-12 col-lg-5 col-md-5 col-sm-12">
            <DisplayErrors errors={props.formErrors} />
            <div className="row">
              <div className="col col-lg-8 col-12">
                <InputGroup size="sm">
                  <FormControl
                    type="text"
                    placeholder="Asset Type"
                    id={props.assetTypeFieldId}
                    value={props.assetTypeFieldValue}
                    name={props.assetTypeFieldId}
                    onChange={props.handleChange}
                  />
                </InputGroup>
                {props.errors.assetType ? (
                  <p className="error-text">{props.errors.assetType}</p>
                ) : null}
              </div>
            </div>
          </div>
          <div className="col-12 col-lg-5 col-md-5 col-sm-12">
            <h5 className="font-weight-bold">Attributes</h5>
            {props.attributes.map((attribute, i) => {
              return (
                <div key={i} className="row">
                  <div className=" col col-11 col-lg-11 col-md-11 mb-3">
                    <div className="row">
                      <div className="col-5">
                        <Form.Control
                          as="select"
                          onChange={e => props.onAssetAttribute(e, i)}
                          value={attribute.id}
                        >
                          <option value="Select a Attribute" disabled>
                            Select a Attribute
                          </option>
                          {props.attributesDropdown.map((value, j) => {
                            let disabled = false;
                            for (const attr of props.attributes) {
                              if (attr.id == value.id) {
                                disabled = 'disabled';
                              }
                            }
                            return (
                              <option
                                value={value.id}
                                key={value.id}
                                id={value.id}
                                disabled={disabled}
                              >
                                {value.name}
                              </option>
                            );
                          })}
                        </Form.Control>
                        {attribute.errors.attribute ? (
                          <p className="error-text">
                            {attribute.errors.attribute}
                          </p>
                        ) : null}
                      </div>
                      {attribute.id !== 'Select a Attribute' ? (
                        <div className="col-5">
                          <Form.Control
                            as="select"
                            onChange={e => {
                              props.onValueSelected(e, i);
                            }}
                            defaultValue={
                              attribute.selectedValue || 'Select a Value'
                            }
                          >
                            <option value="Select a Value" disabled>
                              Select a Value
                            </option>
                            {attribute.values.map(value => {
                              return (
                                <option value={value.id} key={value.id}>
                                  {value.name}
                                </option>
                              );
                            })}
                          </Form.Control>
                          {attribute.errors.value ? (
                            <p className="error-text">
                              {attribute.errors.value}
                            </p>
                          ) : null}
                        </div>
                      ) : null}
                    </div>
                  </div>
                  <div className="col-1">
                    <button
                      className="delete-attribute"
                      onClick={() => props.deleteAttributeCard(i)}
                      disabled={props.attributes.length === 1}
                    >
                      <FontAwesomeIcon
                        className="delete-button"
                        icon={faTrashAlt}
                      />
                    </button>
                  </div>
                </div>
              );
            })}
            <div className="row">
              <div className="float-right col col-3 offset-lg-10">
                <button
                  className="add-more-button"
                  onClick={props.addAttributeCard}
                  disabled={
                    props.attributes.length === props.attributesDropdown.length
                  }
                >
                  Add More...
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
        <CustomButton
        // isLoading={props.inProgress}
        submitFunction={props.handleSubmit}
        id="addEditAssetType"
        className="btn onboarding-btn float-right"
        text={SUBMIT}
        type="submit"
      />
    </div>
  );
};

AddEditAssetTypesForm.propTypes = {
  handleSubmit: PropTypes.func.isRequired,
  formErrors: PropTypes.object.isRequired,
  handleChange: PropTypes.func.isRequired,
  assetTypeFieldId: PropTypes.string.isRequired,
  assetTypeFieldValue: PropTypes.string.isRequired,
  errors: PropTypes.object.isRequired,
  attributes: PropTypes.array.isRequired,
  onValueSelected: PropTypes.func.isRequired,
  deleteAttributeCard: PropTypes.func.isRequired,
  addAttributeCard: PropTypes.func.isRequired,
  onAssetAttribute: PropTypes.func.isRequired,
  attributesDropdown: PropTypes.array.isRequired
};

AddEditAssetTypesForm.defaultProps = {};

export default AddEditAssetTypesForm;
