import { reverse } from 'named-urls';
import { PAGE, STORE,TODOS } from './constants';
import ACTIONS from '../apiConfig/actions.constants';
import { FOOTPRINTS_ROUTES } from '../config';

import {
  getAssetTypes,
  redirect,
  redirectAssetTypeDetail,
  getFomatedOrgUnitData
} from './utils';

export default {
  [PAGE.TODOS]: {
    pageTitle: TODOS,
    headerActions: isAssetAdmin
      ? []
      : [
          {
            text: ADD,
            handler: redirect,
            id: addId
          }
        ],
    actions: {
      fetchList: ACTIONS.ASSET_TYPES.GET_ASSET_TYPES
    },
    store: {
      storedList: STORE.ASSET_TYPES
    },
    bodyHandler: {
      listHandler: redirectAssetTypeDetail
    },
    redirect: {
      [ADD]: reverse(FOOTPRINTS_ROUTES.ASSET_TYPES.ADD)
    },
    formateFunc: getAssetTypes
  },

  [PAGE.ADD_TODO]: {
    pageTitle: ADD_USER,
    headerActions: [],
    actions: {
      postData: ACTIONS.USERS.ADD_USER,
      getAllOrgUnit: ACTIONS.ORG_UNITS.GET_ALL_ORG_UNIT,
      fetchUser: ''
    },
    formate: getFomatedOrgUnitData,
    store: {}
  },

  [PAGE.UPDATE_TODO]: {
    pageTitle: UPDATE_USER,
    headerActions: [],
    actions: {
      postData: ACTIONS.USERS.UPDATE_USER,
      getAllOrgUnit: ACTIONS.ORG_UNITS.GET_ALL_ORG_UNIT,
      fetchUser: ACTIONS.USERS.GET_USER_INFO
    },
    formate: getFomatedOrgUnitData,
    store: {}
  },

};
