import React from 'react';
import pageConfig from '../../utils/pageConfig';
import PageLoader from '../../components/PageLoader/PageLoader';
import PageHeader from '../../components/PageHeader/PageHeader';
import Table from '../../components/Table/Table';
import { Pagination } from 'react-bootstrap';
import { getActionsFormate } from '../../utils/common';

class Assets extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      perPage: 10,
      currentPage: 1,
      prePage: 1,
    }
  }

  componentDidMount() {
    const { getDetials, match, page } = this.props;
    const { actions } = pageConfig[page];
    const { params } = match;
    const type = actions && actions.fetchDetails;
    getAssetsList(type, { ...params });
  }

  render(){
    const { page } = this.props;
    const { pageTitle, headerActions } = pageConfig[page]
    return (
      <div>
        <PageLoader inProgress={false} />
        <PageHeader
          {...this.props}
          title={pageTitle}
        />
        <Table />
        <Pagination className="custom-pagination justify-content-end mt-3">
          <Pagination.First />
          <Pagination.Prev />
          <Pagination.Item>{1}</Pagination.Item>
          <Pagination.Item>{2}</Pagination.Item>
          <Pagination.Ellipsis />
          <Pagination.Item>{20}</Pagination.Item>
          <Pagination.Next />
          <Pagination.Last />
        </Pagination>
      </div>
    );
  }

}

Details.propTypes = {
  Data: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired,
  getDetials: PropTypes.func.isRequired,
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired,
  redirect: PropTypes.func.isRequired,
  inProgress: PropTypes.bool.isRequired
};

const mapStateToProps = state => ({
  Data: state.DetailsReducerStore.data,
  inProgress: state.DetailsReducerStore.inProgress
});

const mapDispatchToProps = dispatch => {
  return {
    getAssetsList: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Assets)
);
