import { reverse } from 'named-urls';
import { PAGE, STORE, ROLES } from './constants';
import ACTIONS from '../apiConfig/actions.constants';
import { FOOTPRINTS_ROUTES } from '../config';
import UserInfo from '../components/UserInfo/UserInfo';
import AssetTypeInfo from '../components/AssetTypesInfo/AssetTypeInfo';
import AssetAttributesInfo from '../components/AssetAttributesInfo/AssetAttributesInfo';

import {
  showUserDetails,
  getOrgUnitsNext,
  getAssetAttributes,
  getAssetTypes,
  getOrgUnits,
  getUserCard,
  redirect,
  redirectAssetTypeDetail,
  redirectAssetAttributeDetail,
  getFomatedOrgUnitData
} from './utils';
import AssetDetails from '../components/AssetDetails/AssetDetails';
import OrgUnitDetails from '../components/OrgUnitDetails/OrgUnitDetails';
import { getAnalyFormOfAsset } from './common';
import { checkUserValidity } from './authService';

const ADD = 'Add';
const EDIT = 'Edit';
const USERS_PAGE_TITLE = 'Users';
const ORG_UNITS_PAGE_TITLE = 'Departments';
const ORG_UNITS_DETAILS_PAGE_TITLE = 'Department Details';
const ASSET_TYPE_PAGE_TITLE = 'Asset Types';
const ASSET_ATTRIBUTES_PAGE_TITLE = 'Asset Attributes';
const ADD_ASSET_TYPES = 'Add Asset Type';
const ADD_ASSET_ATTRIBUTES = 'Add Asset Attributes';
const USER_DETAILS = 'Details';
const ADD_USER = 'Add User';
const UPDATE_USER = 'Update User';
const ADD_ORG_UNIT = 'Add Department';
const UPDATE_ASSET_ATTRIBUTES = 'Update Asset Attributes';
const UPDATE_ASSET_TYPES = 'Update Asset Types';
const addId = 'add';
const editId = 'edit';
const ASSETS = 'Assets';
const ASSET_DETAILS = 'Asset Details';
const ADD_QR_CODES = 'Add';
const ANALYTICS = 'Analytics';
const UPDATE_ORG_UNIT = 'Update Department'
const isAssetAdmin =
checkUserValidity(false, ROLES.ASSET_MANAGER) &&
!checkUserValidity(false, ROLES.SUPER_ADMIN) &&
!checkUserValidity(false, ROLES.ORG_ADMIN);
export default {
  [PAGE.ASSET_TYPES]: {
    pageTitle: ASSET_TYPE_PAGE_TITLE,
    headerActions: isAssetAdmin?[]:[
      {
        text: ADD,
        handler: redirect,
        id: addId
      }
    ],
    actions: {
      fetchList: ACTIONS.ASSET_TYPES.GET_ASSET_TYPES
    },
    store: {
      storedList: STORE.ASSET_TYPES
    },
    bodyHandler: {
      listHandler: redirectAssetTypeDetail
    },
    redirect: {
      [ADD]: reverse(FOOTPRINTS_ROUTES.ASSET_TYPES.ADD)
    },
    formateFunc: getAssetTypes
  },

  [PAGE.USERS]: {
    pageTitle: USERS_PAGE_TITLE,
    headerActions: isAssetAdmin?[]:[
      {
        text: ADD,
        handler: redirect,
        id: addId
      }
    ],
    actions: {
      fetchList: ACTIONS.USERS.GET_USER_LIST
    },
    store: {
      storedList: STORE.USER_LIST
    },
    bodyHandler: {
      listHandler: showUserDetails
    },
    redirect: {
      [ADD]: FOOTPRINTS_ROUTES.USERS.ADD
    },
    formateFunc: getUserCard
  },

  [PAGE.ORG_UNITS]: {
    pageTitle: ORG_UNITS_PAGE_TITLE,
    headerActions: isAssetAdmin?[]:[
      {
        text: ADD,
        handler: redirect,
        id: addId
      }
    ],
    actions: {
      fetchList: ACTIONS.ORG_UNITS.GET_ORG_UNITS_LIST
    },
    store: {
      storedList: STORE.ORG_UNITS_LIST
    },
    bodyHandler: {
      listHandler: getOrgUnitsNext
    },
    redirect: {
      [ADD]: FOOTPRINTS_ROUTES.ORG_UNITS.ADD
    },
    formateFunc: getOrgUnits
  },

  [PAGE.ORG_UNITS_DETAILS]: {
    pageTitle: ORG_UNITS_DETAILS_PAGE_TITLE,
    headerActions: isAssetAdmin?[]:[
      {
        text: ADD,
        handler: redirect,
        id: addId
      },
      {
        text: EDIT,
        handler: redirect,
        id: editId
      }
    ],
    actions: {
      fetchDetails: ACTIONS.ORG_UNITS.GET_ORG_UNITS_DETAILS
    },
    store: {
      storedList: STORE.ORG_UNITS_LIST
    },
    bodyHandler: {
      listHandler: getOrgUnitsNext
    },
    redirect: {
      [ADD]: FOOTPRINTS_ROUTES.ORG_UNITS.ADD,
      [EDIT]: FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.MODIFY
