  },

  [PAGE.ADD_USER]: {
    pageTitle: ADD_USER,
    headerActions: [],
    actions: {
      postData: ACTIONS.USERS.ADD_USER,
      getAllOrgUnit: ACTIONS.ORG_UNITS.GET_ALL_ORG_UNIT,
      fetchUser: ''
    },
    formate: getFomatedOrgUnitData,
    store: {}
  },

  [PAGE.UPDATE_USER]: {
    pageTitle: UPDATE_USER,
    headerActions: [],
    actions: {
      postData: ACTIONS.USERS.UPDATE_USER,
      getAllOrgUnit: ACTIONS.ORG_UNITS.GET_ALL_ORG_UNIT,
      fetchUser: ACTIONS.USERS.GET_USER_INFO
    },
    formate: getFomatedOrgUnitData,
    store: {}
  },

  [PAGE.ADD_ORG_UNIT]: {
    pageTitle: ADD_ORG_UNIT,
    headerActions: [],
    actions: {
      postData: ACTIONS.ORG_UNITS.ADD_ORG_UNIT,
      getAllOrgUnit: ACTIONS.ORG_UNITS.GET_ALL_ORG_UNIT,
      fetchOrgUnitInfo: ''
    },
    formate: getFomatedOrgUnitData
  },

  [PAGE.UPDATE_ORG_UNIT]: {
    pageTitle: UPDATE_ORG_UNIT,
    headerActions: [],
    actions: {
      postData: ACTIONS.ORG_UNITS.UPDATE_ORG_UNIT,
