      fetchOrgUnitInfo: ACTIONS.ORG_UNITS.GET_ORG_UNITS_DETAILS
    },
    formate: getFomatedOrgUnitData,
    store: {}
  },

  [PAGE.ASSET_TYPES_DETAILS]: {
    pageTitle: USER_DETAILS,
    headerActions: [
      {
        text: EDIT,
        handler: redirect,
        id: editId
      }
    ],
    redirect: {
      [EDIT]: FOOTPRINTS_ROUTES.ASSET_TYPES.DETAIL.MODIFY
    },
    component: AssetTypeInfo,
    actions: {
      fetchDetails: ACTIONS.ASSET_TYPES.ASSET_TYPES_DETAILS
    }
  },

  [PAGE.ASSET_ATTRIBUTE_DETAILS]: {
    pageTitle: USER_DETAILS,
    headerActions: [
      {
        text: EDIT,
        handler: redirect,
        id: editId
      }
    ],
    redirect: {
      [EDIT]: FOOTPRINTS_ROUTES.ASSET_ATTRIBUTES.DETAIL.MODIFY
    },
    component: AssetAttributesInfo,
    actions: {
      fetchDetails: ACTIONS.ASSET_ATTRIBUTES.ASSET_ATTRIBUTE_DETAILS
    }
  },

  [PAGE.ADD_ASSET_TYPES]: {
    pageTitle: ADD_ASSET_TYPES,
    headerActions: [],
    actions: {
      postData: ACTIONS.ASSET_TYPES.ADD_ASSET_TYPE,
      fetchAttributes: ACTIONS.ASSET_TYPES.GET_ASSET_ATTRIBUTES,
      fetchDetails: ''
    }
  },

  [PAGE.ADD_ASSET_ATTRIBUTES]: {
    pageTitle: ADD_ASSET_ATTRIBUTES,
    headerActions: [],
    actions: {
      postData: ACTIONS.ASSET_ATTRIBUTES.ADD_ASSET_ATTRIBUTES,
      fetchDetails: ''
    }
  },

  [PAGE.UPDATE_ASSET_ATTRIBUTES]: {
    pageTitle: UPDATE_ASSET_ATTRIBUTES,
    headerActions: [],
    actions: {
      postData: ACTIONS.ASSET_ATTRIBUTES.UPDATE_ASSET_ATTRIBUTES,
      fetchDetails: ACTIONS.ASSET_ATTRIBUTES.ASSET_ATTRIBUTE_DETAILS
    }
  },

  [PAGE.UPDATE_ASSET_TYPES]: {
    pageTitle: UPDATE_ASSET_TYPES,
    headerActions: [],
    actions: {
      postData: ACTIONS.ASSET_TYPES.UPDATE_ASSET_TYPE,
      fetchDetails: ACTIONS.ASSET_TYPES.ASSET_TYPES_DETAILS,
      fetchAttributes: ACTIONS.ASSET_TYPES.GET_ASSET_ATTRIBUTES
    }
  },

  [PAGE.ASSET]: {
    pageTitle: ASSETS,
    headerActions: [],
    actions: {
      fetchList: ACTIONS.ASSETS.GET_ASSETS_LIST
    },
    bodyHandler: {
      listHandler: ''
    },
    formateFunc: ''
  },

  [PAGE.ASSET_DETAILS]: {
    pageTitle: ASSET_DETAILS,
    headerActions: [],
    component: AssetDetails,
    actions: {
      fetchDetails: ACTIONS.ASSETS.GET_ASSET_DETAILS,
      fetchHistory: ACTIONS.TRAKING_LOGS.GET_TRACKING_LOGS
    }
  },

  [PAGE.ADD_QR_CODES]: {
    pageTitle: ADD_QR_CODES,
    headerActions: [],
    actions: {
      postData: ACTIONS.QR_CODES.ADD_QR_CODES,
      fetchList: ACTIONS.ASSET_TYPES.GET_ASSET_TYPES
    }
  },

  [PAGE.ANALYTICS]: {
    pageTitle: ANALYTICS,
    headerActions: [],
    actions: {
      fetchAssetsCount: ACTIONS.ASSET_TYPES.GET_ASSETS_COUNT
    },
    formateFunc: {
      assetTypeCount: getAnalyFormOfAsset
