import React from 'react';
import PropTypes from 'prop-types';
import './AssetDetails.scss';
import { getFormatedDate, isValidData } from '../../utils/common';
import { ASSET_STATUS, LOG_TYPE } from '../../utils/constants';
import DataNotFound from '../DataNotFound/DataNotFound';

const AssetDetails = props => {
  const { Data, historyData } = props;
  if(isValidData(Data)){
    const {
      asset_type: AssetType,
      attributes: Attributes,
      due_date: DueDate,
      state: Status,
      submitted_date: SubmittedDate
    } = Data;
    let customKey = 0;
    const attributeElements =
      Attributes &&
      Attributes.map(each => {
        const { attribute: Attribute, attribute_value: AttributeValue } = each;
        customKey += 1;
       const element =  Attribute && AttributeValue ? 
        (
          <div className="asset-details-body row" key={customKey}>
            <span className="asset-details-key col">{Attribute.name}</span>
            <span className="asset-details-key">:</span>
            <span className="asset-details-value col">{AttributeValue.name}</span>
          </div>
        ): true;

        return element;
      });

    let historyKey = 0;
    const showHistory =
      historyData &&
      historyData instanceof Array &&
      historyData.map(eachLog => {
        historyKey += 1;
        const {
          timestamp,
          attributes,
          log_type: logType,
          agent,
          org_unit: OrgUnit,
          assignee,
          notes
        } = eachLog;
        let agentName = '';
        let assigneeName = '';
        if (agent) {
          const { first_name: firstName, last_name: lastName } = agent;
          agentName = `${firstName} ${lastName}`;
        }

        if (assigneeName) {
          const { first_name: firstName, last_name: lastName } = assignee;
          assigneeName = `${firstName} ${lastName}`;
        }

        let logsAttributs = [];
        if (attributes) {
          logsAttributs =
            attributes instanceof Array &&
            attributes.map(eachAttr => {
              const {
                attribute: Attribute,
                attribute_value: AttributeValue
              } = eachAttr;
              customKey += 1;
              return (
                <div className="history-log-body-value row" key={customKey}>
                  <span className="font-weight-bold col">{Attribute.name}</span>
                  <span className="font-weight-bold">:</span>
                  <span className="col">{AttributeValue.name}</span>
                </div>
              );
            });
        }

        return (
          <div className="history-item row">
            <div className="col col-4 ml-xl-n2 ml-md-n1 ml-n2 date-time">
              {getFormatedDate(timestamp)}
            </div>
            <div
              className={`${
                historyKey === historyData.length ? 'item-data last' : 'item-data'
              } w-100 col col-7`}
            >
              <div className="border p-1 item-data-type">
                <strong>{LOG_TYPE[logType]}</strong>
                {agentName && (
                  <div className="asset-changed-by mb-1">
                    Changed by{' '}
                    <span className="font-weight-bold">{agentName}</span>
                  </div>
                )}
                {logsAttributs.length > 0 && { logsAttributs }}
                {OrgUnit && (
                  <div className="history-log-body-key">
                    Org unit:{' '}
                    <div className="history-log-body-value ml-2">
                      {' '}
                      {OrgUnit.name}{' '}
                    </div>
                  </div>
                )}
                {assigneeName && (
                  <div className="history-log-body-key">
                    assignee:{' '}
                    <div className="history-log-body-value ml-2">
                      {' '}
                      {assigneeName}{' '}
                    </div>
                  </div>
                )}
                {notes && (
                  <div className="history-log-body-key">
                    notes:{' '}
                    <div className="history-log-body-value ml-2">{notes}</div>
                  </div>
                )}
              </div>
            </div>
          </div>
        );
      });
    return (
      <div className="asset-details-page text-capitalize">
        <div className="row">
          <div className="col col-11 col-md-5 ml-2 p-2 details-title">
            {' '}
            Details{' '}
          </div>
          <div className="col col-11 col-md-5 ml-2 p-2 details-title d-none d-md-block">
            {' '}
            History{' '}
          </div>
        </div>
        <div className="row ">
          <div className="col col-11 col-md-5 ml-2 p-2 asset-detials">
            <div className="asset-details-body row">
              <span className="asset-details-key col">asset type</span>
              <span className="asset-details-key">:</span>
              <span className="asset-details-value col">
                {AssetType ? AssetType.name : false}
              </span>
            </div>
            <div className="asset-details-body row">
              <span className="asset-details-key col">Submitted Date</span>
              <span className="asset-details-key">:</span>
              <span className="asset-details-value col">
                {getFormatedDate(SubmittedDate)}
              </span>
            </div>
            <div className="asset-details-body row">
              <span className="asset-details-key col">Due Date</span>
              <span className="asset-details-key">:</span>
              <span className="asset-details-value col">
                {getFormatedDate(DueDate)}
              </span>
            </div>
            <div className="asset-details-body row">
              <span className="asset-details-key col">Status</span>
              <span className="asset-details-key">:</span>
              <span className="asset-details-value col">
                {ASSET_STATUS[Status]}
              </span>
            </div>

            <div id="asset-attributs-title" className="mb-3 mt-3">
              <span>Attributes</span>
            </div>
            {attributeElements && attributeElements.length > 0 ? (
              attributeElements
            ) : (
              <span>No attributes found</span>
            )}
          </div>
          <div className="col d-md-none mb-3 mt-3 details-title"> History </div>
          <div className="card col col-11 col-md-5 ml-3 p-2 asset-history-detials sticky-top">
            <div className="events w-100">
              {showHistory && showHistory.length > 0 ? (
                showHistory
              ) : (
                <DataNotFound name="History" />
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
  return null;

};

AssetDetails.propTypes = {
  Data: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired,
  historyData: PropTypes.oneOfType([PropTypes.array, PropTypes.object])
    .isRequired
};

AssetDetails.defaultProps = {
  Data: {},
  historyData: []
};
export default AssetDetails;
