import React from 'react';
import { faEnvelope } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { InputGroup, FormControl, Button, DropdownButton, Dropdown } from 'react-bootstrap';
import PropTypes from 'prop-types';
import DisplayErrors from '../DisplayErrors';
import DropdownMenu from 'react-bootstrap/DropdownMenu';
import DropdownItem from 'react-bootstrap/DropdownItem';
import './AddEditUserForm.scss'
import CustomSelect from '../CustomSelect/CustomSelect';

const onChange=(event)=>{
  console.log("Data:", event);

}

const AddEditUserForm = props => {
    return (
      <div className="card-text text-left">
        <div className="">
          <form onSubmit={props.handleSubmit}>
            <DisplayErrors errors={props.formErrors} />
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text className="pl-0">
                  {/* <FontAwesomeIcon icon={faEnvelope} /> */}
                </InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                type="text"
                placeholder="First Name"
                id={props.firstNameFieldId}
                value={props.firstNameFieldValue}
                name={props.firstNameFieldId}
                onChange={props.handleChange}
              />
            </InputGroup>
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text className="pl-0">
                  {/* <FontAwesomeIcon icon={faEnvelope} /> */}
                </InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                type="text"
                placeholder="Last Name"
                id={props.lastNameFieldId}
                value={props.lastNameFieldValue}
                name={props.lastNameFieldId}
                onChange={props.handleChange}
              />
            </InputGroup>
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text className="pl-0">
                  {/* <FontAwesomeIcon icon={faEnvelope} /> */}
                </InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                type="email"
                placeholder="Email"
                id={props.emailFieldId}
                value={props.emailFieldValue}
                name={props.emailFieldName}
                onChange={props.handleChange}
              />
            </InputGroup>
                <CustomSelect 
                  onChange = {onChange}
                  departmentsFieldValue = {props.departmentsFieldValue}
                  departmentsFieldId = {props.departmentsFieldId}
                  selectedDepartments = {props.selectedDepartments}
                />            
            <Button

              type="submit"
              className="btn onboarding-btn"
              disabled={props.disabled}
            >
              Submit
            </Button>
          </form>
        </div>
      </div>
    );
  };

  AddEditUserForm.propTypes = {
    handleSubmit: PropTypes.func.isRequired,
    formErrors: PropTypes.object.isRequired,
    emailFieldId: PropTypes.string.isRequired,
    emailFieldValue: PropTypes.string.isRequired,
    handleChange: PropTypes.func.isRequired,
    firstNameFieldId: PropTypes.string.isRequired,
    firstNameFieldValue: PropTypes.string.isRequired,
    lastNameFieldId: PropTypes.string.isRequired,
    lastNameFieldValue: PropTypes.string.isRequired
  };

  export default AddEditUserForm;
