import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import PageHeader from '../../components/PageHeader/PageHeader';
import pageConfig from '../../utils/pageConfig';
import { getActionsFormate } from '../../utils/common';
import AddEditOrgUnitsForm from '../../components/AddEditOrgUnitsForm/AddEditOrgUnitsForm';
import ACTIONS from '../../apiConfig/actions.constants';
import { getErrorResMsg } from '../../utils/formValidations';
import { PAGE, STORE } from '../../utils/constants';
import PageLoader from '../../components/PageLoader/PageLoader';

class AddEditOrgUnits extends React.Component {
  ORG_UNIT_FIELD_ID = 'org-unit-name';

  PARENT_FIELD_ID = 'parent-field';

  SELECTED_PARENT_ID = 'selected-parent';

  constructor(props) {
    super(props);
    this.state = {
      formErrors: {},
      formValidity: false,
      [this.ORG_UNIT_FIELD_ID]: '',
      [this.PARENT_FIELD_ID]: '',
      errorCode: '',
      disabled: true,
      data: [],
      dropdownMode: 'radioSelect',
      [this.SELECTED_PARENT_ID]: [],
      firstRender: true
    };
  }

  componentDidMount() {
    const { page, getOrgUnitDetails, match } = this.props;
    const utype = pageConfig[page].actions.fetchOrgUnitInfo;
    if (utype) {
      const { params } = match;
      getOrgUnitDetails(utype, { ...params, page });
    }
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    const { errorCode } = nextProps;
    if (errorCode && errorCode !== prevState.errorCode) {
      const errMsg = getErrorResMsg(errorCode, PAGE.ADD_ORG_UNIT);
      return { formErrors: errMsg, errorCode };
    }
    if (
      nextProps.page === PAGE.UPDATE_ORG_UNIT &&
      nextProps.orgUnitDetails &&
      Object.values(nextProps.orgUnitDetails).length > 0 &&
      prevState.firstRender
    ) {
      const { name, parent } = nextProps.orgUnitDetails;
      const valExist = parent && Object.values(parent).length > 0;
      return {
        'org-unit-name': name,
        'selected-parent': valExist ? [parent] : [],
        'parent-field': valExist ? parent.id : '',
        firstRender: false
      };
    }
    return prevState;
  }

  enableSubmit = (orgUnitName) => {
    const disabled = !(orgUnitName.length > 0);
    if (this.props.errorCode) {
      const type = ACTIONS.ORG_UNITS.RESET_STATE;
      this.props.resetState(type);
      this.setState({ errorCode: '' });
    }
    this.setState({ disabled });
  };

  handleChange = event => {
    this.setState(
      {
        [event.target.id]: event.target.value,
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.ORG_UNIT_FIELD_ID],
        )
    );
  };

  handleParents = (currentNode) => {
    if (currentNode.checked && currentNode.orgUnitId) {
      this.setState(
        {
          [this.PARENT_FIELD_ID]: currentNode.orgUnitId,
          formErrors: {}
        },
        () =>
          this.enableSubmit(
            this.state[this.ORG_UNIT_FIELD_ID],
          )
      );
    } else {
      this.setState(
        {
          [this.PARENT_FIELD_ID]: '',
          formErrors: {}
        },
        () =>
          this.enableSubmit(
            this.state[this.ORG_UNIT_FIELD_ID],
          )
      );
    }
  };

  handleSubmit = event => {
    event.preventDefault();

    let data = {};

    if(this.state[this.PARENT_FIELD_ID]){
      data = {
        name: this.state[this.ORG_UNIT_FIELD_ID],
        parent: this.state[this.PARENT_FIELD_ID]
      };
    }else{
      data = {
        name: this.state[this.ORG_UNIT_FIELD_ID],
      };
    }
    const type = pageConfig[this.props.page].actions.postData;
    this.props.submit(type, { data, props: { ...this.props } });
  };

  render() {
    const { page, inProgress } = this.props;
    const { pageTitle } = pageConfig[page];
    const showPageLoader =
      inProgress && this.state.disabled && page === PAGE.UPDATE_ORG_UNIT;
    return (
      <div className="add-edit-user" id="add-edit-org-unit-page">
        <PageLoader inProgress={showPageLoader} />
        <PageHeader title={pageTitle} />
        <AddEditOrgUnitsForm
          handleSubmit={this.handleSubmit}
          formErrors={this.state.formErrors}
          orgUnitFieldId={this.ORG_UNIT_FIELD_ID}
          orgUnitFieldValue={this.state[this.ORG_UNIT_FIELD_ID]}
          handleParents={this.handleParents}
          parentFieldIds={this.PARENT_FIELD_ID}
          handleChange={this.handleChange}
          disabled={this.state.disabled}
          page={page}
          mode={this.state.dropdownMode}
          prevPrentOrgUnit={this.state[this.SELECTED_PARENT_ID]}
          inProgress={inProgress}
        />
      </div>
    );
  }
}

AddEditOrgUnits.propTypes = {
  page: PropTypes.string.isRequired,
  submit: PropTypes.func.isRequired,
  errorCode: PropTypes.string.isRequired,
  resetState: PropTypes.func.isRequired,
  getOrgUnitDetails: PropTypes.func.isRequired,
  match: PropTypes.object.isRequired,
  orgUnitDetails: PropTypes.object.isRequired,
  inProgress: PropTypes.bool.isRequired
};

AddEditOrgUnits.defaultProps = {};

const mapStateToProps = state => {
  return {
    errorCode: state.addEditOrgUnitReducerStore.errorCode,
    orgUnitDetails: state.listReducerStore[STORE.ORG_UNITS_DETAILS],
    inProgress: state.addEditOrgUnitReducerStore.inProgress
  };
};

const mapDispatchToProps = dispatch => {
  return {
    submit: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    },
    getOrgUnitDetails: (type, data) => {
      dispatch(getActionsFormate(type, data));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(AddEditOrgUnits)
);
