import React from 'react';
import PropTypes from 'prop-types';
import { faUser } from '@fortawesome/free-regular-svg-icons';
import { faUnlockAlt } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { InputGroup, FormControl, Button } from 'react-bootstrap';
import DisplayErrors from '../DisplayErrors';
import Checkbox from '../Checkbox/Checkbox';

import './LoginForm.scss';
import { LOGIN_BUTTON_TEXT } from '../../utils/constants';
import ButtonLoader from '../ButtonLoader/ButtonLoader';

const LoginForm = props => {
  return (
    <div className="login-form">
      <form onSubmit={props.handleSubmit} autoComplete="off">
        <div className="login-form">
          <DisplayErrors errors={props.formErrors} />
          <InputGroup>
            <InputGroup.Prepend>
              <InputGroup.Text>
                <FontAwesomeIcon icon={faUser} />
              </InputGroup.Text>
            </InputGroup.Prepend>
            <FormControl
              autoFocus
              type="email"
              placeholder="Username"
              id={props.emailFieldId}
              value={props.emailValue}
              name={props.emailFieldId}
              onChange={props.handleChange}
              onKeyDown={props.handleInvalidKeys}
            />
          </InputGroup>
          <InputGroup>
            <InputGroup.Prepend>
              <InputGroup.Text>
                <FontAwesomeIcon icon={faUnlockAlt} />
              </InputGroup.Text>
            </InputGroup.Prepend>
            <FormControl
              type="password"
              placeholder="Password"
              id={props.passwordFieldId}
              value={props.passwordValue}
              name={props.passwordFieldId}
              onChange={props.handleChange}
            />
          </InputGroup>
          <div className="row">
            <div className="col col-6">
              <Checkbox
                id="rememberMe"
                checked={props.rememberMe}
                onCheck={props.handleRememberMe}
                labelText="Remember Me"
              />
            </div>
            <div
              className="col col-6 text-right clickable forgot-password"
              onClick={props.handleForgottenPassword}
              role="button"
              id="forgot-password-link"
            >
              Forgot Password
            </div>
          </div>
          {/* <img src={loader} alt="loading" /> */}
          <div className="" />
          <Button
            block
            id="fp-login-submit"
            type="submit"
            className="btn onboarding-btn"
            disabled={props.disabled || props.inProgress}
          >
            {props.inProgress && <ButtonLoader />}
            {!props.inProgress && LOGIN_BUTTON_TEXT}
          </Button>
        </div>
      </form>
    </div>
  );
};

LoginForm.propTypes = {
  formErrors: PropTypes.object,
  emailValue: PropTypes.string,
  handleSubmit: PropTypes.func,
  handleChange: PropTypes.func,
  disabled: PropTypes.bool,
  emailFieldId: PropTypes.string,
  passwordValue: PropTypes.string,
  handleInvalidKeys: PropTypes.func,
  passwordFieldId: PropTypes.string,
  rememberMe: PropTypes.bool,
  handleRememberMe: PropTypes.func,
  handleForgottenPassword: PropTypes.func,
  inProgress: PropTypes.bool
};

export default LoginForm;
