import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import GenerateQrCodesForm from '../../components/GenerateQrCodesForm/GenerateQrCodesForm';
import { PAGE } from '../../utils/constants';
import { getErrorResMsg } from '../../utils/formValidations';
import pageConfig from '../../utils/pageConfig';
import { getActionsFormate } from '../../utils/common';
import PageHeader from '../../components/PageHeader/PageHeader';
import ACTIONS from '../../apiConfig/actions.constants';

class GenerateQrCodes extends React.Component {
  ASSET_TYPE_FIELD_ID = 'asset-type';

  QR_CODES_COUNT_FIELD_ID = 'qr-codes-count';

  constructor(props) {
    super(props);
    this.state = {
      formErrors: {},
      formValidity: false,
      [this.ASSET_TYPE_FIELD_ID]: '',
      [this.QR_CODES_COUNT_FIELD_ID]: '',
      errorCode: '',
      disabled: true
    };
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    const { errorCode } = nextProps;
    if (errorCode && errorCode !== prevState.errorCode) {
      const errMsg = getErrorResMsg(errorCode, PAGE.ADD_QR_CODES);
      return { formErrors: errMsg, errorCode };
    }
    return prevState;
  }

  enableSubmit = (assetType, count) => {
    const disabled = !(assetType.length > 0 && count.length > 0);

    if (this.props.errorCode) {
      const type = ACTIONS.ADD_QR_CODES.RESET_STATE;
      this.props.resetState(type);
      this.setState({ errorCode: '' });
    }
    this.setState({ disabled });
  };

  handleChange = event => {
    this.setState(
      {
        [event.target.id]: event.target.value,
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.ASSET_TYPE_FIELD_ID],
          this.state[this.QR_CODES_COUNT_FIELD_ID]
        )
    );
  };

  handleSubmit = event => {
    event.preventDefault();
    const data = {
      asset_type: Number(this.state[this.ASSET_TYPE_FIELD_ID]),
      count: Number(this.state[this.QR_CODES_COUNT_FIELD_ID])
    };
    const type = pageConfig[this.props.page].actions.postData;
    this.props.submit(type, data);
  };

  render() {
    const { generateQrCodesStore, page } = this.props;
    const { inProgress, data } = generateQrCodesStore;
    const { pageTitle } = pageConfig[page];
    return (
      <div className="add-edit-user" id="add-edit-user-page">
        <PageHeader title={pageTitle} />
        {data && data.file_url && (
          <a className="generate-pdf mb-2" href={data.file_url}>
            Show Qr Codes
          </a>
        )}
        <GenerateQrCodesForm
          handleSubmit={this.handleSubmit}
          handleChange={this.handleChange}
          disabled={this.state.disabled}
          assetTypeId={this.ASSET_TYPE_FIELD_ID}
          assetTypeValue={this.state[this.ASSET_TYPE_FIELD_ID]}
          qrCodesCountId={this.QR_CODES_COUNT_FIELD_ID}
          qrCodesCountValue={this.state[this.QR_CODES_COUNT_FIELD_ID]}
          inProgress={inProgress}
        />
      </div>
    );
  }
}

GenerateQrCodes.propTypes = {
  page: PropTypes.string.isRequired,
  submit: PropTypes.func.isRequired,
  errorCode: PropTypes.oneOfType([PropTypes.string, PropTypes.number])
    .isRequired,
  resetState: PropTypes.func.isRequired,
  inProgress: PropTypes.bool.isRequired,
  generateQrCodesStore: PropTypes.object.isRequired
};

GenerateQrCodes.defaultProps = {};

const mapStateToProps = state => ({
  generateQrCodesStore: state.generateQrReducerStore
});

const mapDispatchToProps = dispatch => {
  return {
    submit: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(GenerateQrCodes)
);
