import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { matchPath } from 'react-router'
import { withRouter, NavLink } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import {
  faBars,
  faAngleDoubleLeft,
  faAngleDoubleRight
} from '@fortawesome/free-solid-svg-icons';

import './SideNav.scss';
import { SIDENAV_ITEMS, FOOTPRINTS_ROUTES, ASSET_MNAGER_URLS } from '../../config';
import BreadCrumb from '../../containers/BreadCrumb/BreadCrumb';
import { PAGE, ROLES } from '../../utils/constants';
import { checkUserValidity } from '../../utils/authService';
import AlertMessage from '../AlertMessage/AlertMessage';
import { reverse } from 'named-urls';
import ACTIONS from '../../apiConfig/actions.constants';
import { getActionsFormate } from '../../utils/common';

const SideNav = props => {
  console.log("Props:", props);

  const { children, location, page, orgnizationName, Data } = props;
  let OrgName = orgnizationName;
  let isAssetAdmin = checkUserValidity(false, ROLES.ASSET_MANAGER) &&!checkUserValidity(false, ROLES.SUPER_ADMIN) && !checkUserValidity(false, ROLES.ORG_ADMIN);
  if(Data && Data.name){
    OrgName = Data.name
  }
  const { pathname } = location;
  const sidebarId = 'sidebar';
  const contentId = 'content';
  const expandClassName = 'expanded';
  const showBreadcrumbs = (page === PAGE.ORG_UNITS_DETAILS || isAssetAdmin);
  const toggleSidebar = event => {
    event.preventDefault();
    const sidebar = document.getElementById(sidebarId);
    sidebar.classList.toggle(expandClassName);
  };

  const hideSidebar = event => {
    event.preventDefault();
    const sidebar = document.getElementById(sidebarId);
    sidebar.classList.toggle(expandClassName);
  };

  let orgUnitId = '';

  for(let url of ASSET_MNAGER_URLS){
    const match = matchPath(pathname, {
      path: url,
      exact: true,
      strict: false
    })
    if(match){
      orgUnitId = match.params.orgUnitId;
      break;
    }
  }

  // if(isAssetAdmin && orgUnitId){
  //   getDetials(ACTIONS.ORG_UNITS.GET_ORG_UNITS_DETAILS, {orgUnitId})
  // } 

  const sidenavItems = SIDENAV_ITEMS.map(item => {
    let NavTo = item.route;
    if(isAssetAdmin && orgUnitId && NavTo === FOOTPRINTS_ROUTES.ASSET.LIST){
        NavTo = reverse(FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.ASSET.LIST, {orgUnitId});
    }

    if(isAssetAdmin && orgUnitId && (NavTo === FOOTPRINTS_ROUTES.ORG_UNITS.LIST)){
      NavTo = reverse(FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.SHOW, {orgUnitId});
    }

    if(isAssetAdmin && orgUnitId && NavTo === FOOTPRINTS_ROUTES.ANALYTICS){
      NavTo = reverse(FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.ANALYTICS, {orgUnitId});
    }

    return checkUserValidity(item.page) ? (
      <NavLink
        strict
        exact
        to={NavTo}
        key={item.position}
        className="nav-link"
        activeClassName="selected"
        prevPath={pathname}
        id={item.id}
      >
        <FontAwesomeIcon icon={item.icon} size="lg" />
        <span className="sidenav-item-text">{item.displayName}</span>
      </NavLink>
    ) : (
      true
    );
  });


  const sidenavBar = (
    <nav className="sidebar" id={sidebarId}>
      <ul className="list-unstyled components">
        <div className="sidebar-dismiss" role="button" onClick={hideSidebar}>
          <FontAwesomeIcon icon={faAngleDoubleLeft} size="lg" />
        </div>
        <div className="header">{OrgName || 'Org Unit Name'} </div>
        <div
          className="header-small-width"
          onClick={toggleSidebar}
          role="button"
        >
          <FontAwesomeIcon
            id="arrow-expand"
            icon={faAngleDoubleRight}
            size="lg"
          />
        </div>
        {sidenavItems}
      </ul>
    </nav>
  );

  const contentComponent = (
    <div className="content" id={contentId}>
      <div className="container">
        <nav
          className={`navbar navbar-expand-lg breadcrumbs-nav  ${
            showBreadcrumbs ? 'mb-3' : 'border-0 mb-3'
          }`}
        >
          <button
            type="button"
            id="sidebarCollapse"
            onClick={toggleSidebar}
            className="icon-btn sidebar-collapse-btn"
          >
            <FontAwesomeIcon icon={faBars} size="lg" />
          </button>
          {showBreadcrumbs ? <BreadCrumb /> : null}
        </nav>
        {children}
      </div>
    </div>
  );

  return (
    <div className="sidenav">
      {sidenavBar}
      <AlertMessage />
      {contentComponent}
    </div>
  );
};

const mapStateToProps = state => ({
  Data: state.DetailsReducerStore.data,
});

const mapDispatchToProps = dispatch => {
  return {
    getDetials: (dtype, data) => {
      dispatch(getActionsFormate(dtype, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

SideNav.propTypes = {
  children: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired,
  page: PropTypes.string.isRequired,
  location: PropTypes.object.isRequired,
  orgnizationName: PropTypes.string.isRequired
};

export default withRouter(
  connect(mapStateToProps,mapDispatchToProps)(SideNav));
