import React from 'react';
import PropTypes from 'prop-types';
import './Todo.scss';

class Todo extends React.Component {
    constructor(props){
        super(props);
        this.state={
            showDescription: false
        }
    }

    handleDescription = ()=>{
        this.setState({showDescription:!this.state.showDescription})
    }

    render(){
        const {data} = this.props;
        return (<div className="card col col-12 border m-2 p-3 todo" onClick={eve=>this.handleDescription()}>
            <div className="p-1 todo-title">{data.title}</div>
            {this.state.showDescription && <div className="p-1 todo-description">{data.description}</div>}  
        </div>)
    }
}

Todo.propTypes = {
    data: PropTypes.object,
    paginationData: PropTypes.object.isRequired
};

Todo.defaultProps = {
    data: {}
};
  export default Todo;
