import { call, put } from 'redux-saga/effects';

import { reverse } from 'named-urls';
import ACTIONS from '../../apiConfig/actions.constants';
import { FOOTPRINTS_API } from '../../apiConfig/api.config';

function* getBreadcrumb(action) {
  let response;
  const id = action.data;
  try {
    response = yield call(
      window.axios.getData,
      reverse(FOOTPRINTS_API.BREADCRUMBS, { id }),
      action.data
    );
    if (response.status === 200) {
      yield put({
        type: ACTIONS.BREADCRUMB.GET_BREADCRUMB_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.BREADCRUMB.GET_BREADCRUMB_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.BREADCRUMB.GET_BREADCRUMB_FAIL, payload: e });
  }
}

const dummy = () => {};

export { getBreadcrumb, dummy };
