import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import './Details.scss';
import { getActionsFormate } from '../../utils/common';
import pageConfig from '../../utils/pageConfig';
import PageHeader from '../../components/PageHeader/PageHeader';
import { PAGE, DATA_NOT_FOUND } from '../../utils/constants';
import PageLoader from '../../components/PageLoader/PageLoader';
import { getBodyComponenet } from '../../utils/utils';
import ACTIONS from '../../apiConfig/actions.constants';

class Details extends React.Component {
  componentDidMount() {
    const { getDetials, match, page, getHistory } = this.props;
    const { actions } = pageConfig[page];
    const { params } = match;
    const dType = actions && actions.fetchDetails;
    const hType = actions && actions.fetchHistory;
    if (hType) {
      getHistory(hType, { ...params, page });
    }
    getDetials(dType, { ...params, page });
  }

  componentWillUnmount() {
    this.props.resetState(ACTIONS.DETAILS.RESET_STATE);
  }

  render() {
    const { page, Data, inProgress, match } = this.props;
    const {
      pageTitle,
      headerActions,
      component: Component,
      redirect
    } = pageConfig[page];
    const { params } = match;
    const isDetailsFound = Data && Object.values(Data).length > 0;
    const fullName =
      page === PAGE.USER_DETAILS && Data && (Data.first_name || Data.last_name)
        ? `${Data.first_name} ${Data.last_name}`
        : '';
    const assetAttributeTitle =
      page === PAGE.ASSET_ATTRIBUTE_DETAILS && Data ? Data.name : '';
    const assetTypeTitle =
      page === PAGE.ASSET_TYPES_DETAILS && Data ? Data.doc_type : '';
    const AssetDetailsTitle =
      page === PAGE.ASSET_DETAILS && params.assetId
        ? params.assetId.slice(0, 7)
        : '';
    const OrgUnitDetailsTitle = page === PAGE.ORG_UNITS_DETAILS && Data ? Data.name : '';

    const title = isDetailsFound
      ? fullName ||
        assetAttributeTitle ||
        assetTypeTitle ||
        AssetDetailsTitle ||
        OrgUnitDetailsTitle ||
        pageTitle
      : pageTitle;

    return (
      <div className="details" id="show-details">
        <PageLoader inProgress={inProgress} />
        <PageHeader
          {...this.props}
          title={title}
          actions={headerActions}
          redirect={redirect}
        />
        {getBodyComponenet(
          <Component {...this.props} Data={Data} />,
          isDetailsFound,
          DATA_NOT_FOUND[page]
        )}
      </div>
    );
  }
}

Details.propTypes = {
  Data: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired,
  getDetials: PropTypes.func.isRequired,
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired,
  redirect: PropTypes.func.isRequired,
  inProgress: PropTypes.bool.isRequired,
  getHistory: PropTypes.func.isRequired,
  historyData: PropTypes.oneOfType([PropTypes.array, PropTypes.object])
    .isRequired,
  resetState: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  Data: state.DetailsReducerStore.data,
  inProgress: state.DetailsReducerStore.inProgress,
  historyData: state.commonReducerStore.data
});

const mapDispatchToProps = dispatch => {
  return {
    getDetials: (dtype, data) => {
      dispatch(getActionsFormate(dtype, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    },
    getHistory: (hType, data) => {
      dispatch(getActionsFormate(hType, data));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Details)
);
